import React, { useEffect } from 'react';
import { 
  Layout as AntLayout, 
  Menu, 
  Button, 
  Space, 
  Badge, 
  Avatar, 
  Dropdown,
  Typography,
  Drawer,
  List,
  Image,
  InputNumber,
  Empty,
  Tag
} from 'antd';
import { 
  ShoppingCartOutlined, 
  UserOutlined, 
  LoginOutlined,
  LogoutOutlined,
  FileTextOutlined,
  SearchOutlined,
  DeleteOutlined
} from '@ant-design/icons';
import { useDispatch, useSelector } from 'react-redux';
import { useNavigate, useLocation } from 'react-router-dom';
import { RootState, AppDispatch } from '../store';
import { 
  showLoginModal, 
  clearCurrentUser 
} from '../store/slices/userSlice';
import { 
  openCart, 
  closeCart,
  loadCartAsync,
  updateQuantityAsync,
  removeFromCartAsync
} from '../store/slices/cartSlice';
import SearchBar from './SearchBar';
import LoginModal from './LoginModal';
import RegisterModal from './RegisterModal';

const { Header, Content } = AntLayout;
const { Title, Text } = Typography;

interface LayoutProps {
  children: React.ReactNode;
}

const Layout: React.FC<LayoutProps> = ({ children }) => {
  const navigate = useNavigate();
  const location = useLocation();
  const dispatch = useDispatch<AppDispatch>();
  
  const { currentUser, isLoggedIn } = useSelector((state: RootState) => state.user);
  const { items, totalQuantity, totalAmount, isOpen } = useSelector((state: RootState) => state.cart);
  
  // 加载购物车数据
  useEffect(() => {
    dispatch(loadCartAsync());
  }, [dispatch]);
  
  // 处理用户菜单点击
  const handleUserMenuClick = ({ key }: { key: string }) => {
    switch (key) {
      case 'orders':
        navigate('/orders');
        break;
      case 'logout':
        dispatch(clearCurrentUser());
        navigate('/');
        break;
      default:
        break;
    }
  };
  
  // 用户下拉菜单
  const userMenu = (
    <Menu onClick={handleUserMenuClick}>
      <Menu.Item key="orders" icon={<FileTextOutlined />}>
        我的订单
      </Menu.Item>
      <Menu.Divider />
      <Menu.Item key="logout" icon={<LogoutOutlined />}>
        退出登录
      </Menu.Item>
    </Menu>
  );
  
  // 获取当前页面菜单选中状态
  const getSelectedMenuKey = () => {
    const path = location.pathname;
    if (path === '/') return 'home';
    if (path.startsWith('/search')) return 'search';
    if (path.startsWith('/cart')) return 'cart';
    if (path.startsWith('/orders')) return 'orders';
    return 'home';
  };
  
  // 处理购物车数量变化
  const handleQuantityChange = async (productId: number, quantity: number) => {
    if (quantity > 0) {
      await dispatch(updateQuantityAsync({ productId, quantity }));
    }
  };
  
  // 处理删除购物车商品
  const handleRemoveItem = async (productId: number) => {
    await dispatch(removeFromCartAsync(productId));
  };
  
  // 前往购物车页面
  const goToCart = () => {
    dispatch(closeCart());
    navigate('/cart');
  };
  
  return (
    <AntLayout style={{ minHeight: '100vh' }}>
      <Header style={{ 
        display: 'flex', 
        alignItems: 'center', 
        justifyContent: 'space-between',
        backgroundColor: '#fff',
        borderBottom: '1px solid #f0f0f0',
        padding: '0 24px'
      }}>
        {/* 左侧Logo和主导航 */}
        <div style={{ display: 'flex', alignItems: 'center', flex: 1 }}>
          <div 
            style={{ 
              marginRight: 32, 
              cursor: 'pointer',
              display: 'flex',
              alignItems: 'center'
            }}
            onClick={() => navigate('/')}
          >
            <Title level={3} style={{ margin: 0, color: '#1890ff' }}>
              招财商城
            </Title>
          </div>
          
          <Menu 
            mode="horizontal" 
            selectedKeys={[getSelectedMenuKey()]}
            style={{ 
              border: 'none',
              backgroundColor: 'transparent',
              marginRight: 32
            }}
          >
            <Menu.Item key="home" onClick={() => navigate('/')}>
              首页
            </Menu.Item>
          </Menu>
          
          {/* 搜索框 */}
          <div style={{ flex: 1, maxWidth: 400 }}>
            <SearchBar 
              placeholder="搜索商品..." 
              size="middle"
            />
          </div>
        </div>
        
        {/* 右侧用户操作 */}
        <Space size="large">
          {/* 购物车 */}
          <Badge count={totalQuantity} size="small">
            <Button 
              type="text" 
              icon={<ShoppingCartOutlined />} 
              size="large"
              onClick={() => dispatch(openCart())}
            >
              购物车
            </Button>
          </Badge>
          
          {/* 用户信息 */}
          {isLoggedIn && currentUser ? (
            <Dropdown overlay={userMenu} placement="bottomRight">
              <Space style={{ cursor: 'pointer' }}>
                <Avatar icon={<UserOutlined />} />
                <span>{currentUser.username}</span>
              </Space>
            </Dropdown>
          ) : (
            <Button 
              type="primary" 
              icon={<LoginOutlined />}
              onClick={() => dispatch(showLoginModal())}
            >
              登录
            </Button>
          )}
        </Space>
      </Header>
      
      <Content style={{ backgroundColor: '#f5f5f5' }}>
        {children}
      </Content>
      
      {/* 购物车抽屉 */}
      <Drawer
        title={`购物车 (${totalQuantity}件商品)`}
        placement="right"
        onClose={() => dispatch(closeCart())}
        open={isOpen}
        width={400}
        footer={
          <div style={{ padding: '10px 0' }}>
            <div style={{ display: 'flex', justifyContent: 'space-between', marginBottom: 16 }}>
              <Text strong>总计:</Text>
              <Text strong style={{ color: '#ff4d4f', fontSize: 18 }}>
                ¥{totalAmount.toFixed(2)}
              </Text>
            </div>
            <Space style={{ width: '100%', justifyContent: 'center' }}>
              <Button onClick={() => dispatch(closeCart())}>
                继续购物
              </Button>
              <Button type="primary" onClick={goToCart} disabled={items.length === 0}>
                去结算
              </Button>
            </Space>
          </div>
        }
      >
        {items.length > 0 ? (
          <List
            itemLayout="horizontal"
            dataSource={items}
            renderItem={item => (
              <List.Item
                actions={[
                  <Button
                    type="text"
                    danger
                    icon={<DeleteOutlined />}
                    onClick={() => handleRemoveItem(item.product.id)}
                  />
                ]}
              >
                <List.Item.Meta
                  avatar={
                    <Image
                      width={60}
                      height={60}
                      src={item.product.imageUrl || 'https://via.placeholder.com/60x60?text=暂无图片'}
                      alt={item.product.name}
                      style={{ objectFit: 'cover' }}
                    />
                  }
                  title={
                    <div style={{ cursor: 'pointer' }} onClick={() => navigate(`/product/${item.product.id}`)}>
                      {item.product.name}
                    </div>
                  }
                  description={
                    <div>
                      <Text type="danger">¥{item.product.price}</Text>
                      <div style={{ marginTop: 8 }}>
                        <InputNumber
                          min={1}
                          max={item.product.stockQuantity}
                          value={item.quantity}
                          onChange={(value) => handleQuantityChange(item.product.id, value || 1)}
                          size="small"
                          style={{ width: 60 }}
                        />
                      </div>
                    </div>
                  }
                />
              </List.Item>
            )}
          />
        ) : (
          <Empty
            image={<ShoppingCartOutlined style={{ fontSize: 64, color: '#ccc' }} />}
            description="购物车是空的"
          />
        )}
      </Drawer>
      
      {/* 模态框 */}
      <LoginModal />
      <RegisterModal />
    </AntLayout>
  );
};

export default Layout;
