import React, { useState, useEffect } from 'react';
import { 
  Card, 
  Table, 
  Tabs, 
  Button, 
  Space, 
  Typography, 
  Tag, 
  Modal,
  Descriptions,
  List,
  Image,
  Empty,
  message,
  Spin
} from 'antd';
import { EyeOutlined, FileTextOutlined, ShoppingCartOutlined } from '@ant-design/icons';
import { useNavigate } from 'react-router-dom';
import { Order, OrderStatus, PaymentStatus, PaymentMethod, OrderItem } from '../types/order';
import { orderApi } from '../services/orderApi';

const { Title, Text } = Typography;

const OrderPage: React.FC = () => {
  const navigate = useNavigate();
  const [orders, setOrders] = useState<Order[]>([]);
  const [loading, setLoading] = useState(false);
  const [selectedOrder, setSelectedOrder] = useState<Order | null>(null);
  const [detailVisible, setDetailVisible] = useState(false);
  const [activeTab, setActiveTab] = useState('all');

  useEffect(() => {
    loadOrders();
  }, []);

  // 加载订单列表
  const loadOrders = async () => {
    try {
      setLoading(true);
      
      // 调用真实的API获取订单列表
      const userId = 1; // TODO: 从用户状态获取真实的用户ID
      const response = await orderApi.getUserOrders(userId);
      
      console.log('订单列表API响应:', response);
      console.log('响应数据类型:', typeof response.data);
      console.log('响应数据是否为数组:', Array.isArray(response.data));
      console.log('响应数据内容:', response.data);
      
      if (response && response.data && Array.isArray(response.data)) {
        console.log('设置订单数据，数量:', response.data.length);
        setOrders(response.data);
      } else {
        console.warn('API响应数据格式不正确，使用空数组');
        setOrders([]);
      }
    } catch (error) {
      console.error('加载订单失败:', error);
      message.error('加载订单失败');
      setOrders([]);
    } finally {
      setLoading(false);
    }
  };

  // 获取订单状态标签
  const getOrderStatusTag = (status: OrderStatus) => {
    const statusMap = {
      [OrderStatus.PENDING_PAYMENT]: { color: 'orange', text: '待付款' },
      [OrderStatus.PAID]: { color: 'blue', text: '已付款' },
      [OrderStatus.SHIPPED]: { color: 'cyan', text: '已发货' },
      [OrderStatus.COMPLETED]: { color: 'green', text: '已完成' },
      [OrderStatus.CANCELLED]: { color: 'red', text: '已取消' }
    };
    
    const config = statusMap[status];
    return <Tag color={config.color}>{config.text}</Tag>;
  };

  // 获取支付状态标签
  const getPaymentStatusTag = (status: PaymentStatus) => {
    const statusMap = {
      [PaymentStatus.UNPAID]: { color: 'red', text: '未付款' },
      [PaymentStatus.PAID]: { color: 'green', text: '已付款' },
      [PaymentStatus.PARTIAL_PAID]: { color: 'orange', text: '部分付款' },
      [PaymentStatus.REFUNDED]: { color: 'purple', text: '已退款' }
    };
    
    const config = statusMap[status];
    return <Tag color={config.color}>{config.text}</Tag>;
  };

  // 过滤订单
  const filteredOrders = orders.filter(order => {
    if (activeTab === 'all') return true;
    if (activeTab === 'pending') return order.orderStatus === OrderStatus.PENDING_PAYMENT;
    if (activeTab === 'paid') return order.paymentStatus === PaymentStatus.PAID && order.orderStatus !== OrderStatus.COMPLETED;
    if (activeTab === 'shipped') return order.orderStatus === OrderStatus.SHIPPED;
    if (activeTab === 'completed') return order.orderStatus === OrderStatus.COMPLETED;
    return true;
  });

  // 订单操作
  const handleOrderAction = async (action: string, orderId: number) => {
    try {
      switch (action) {
        case 'cancel':
          await orderApi.cancelOrder(orderId);
          message.success('订单取消成功');
          loadOrders();
          break;
        case 'confirm':
          await orderApi.confirmReceived(orderId);
          message.success('确认收货成功');
          loadOrders();
          break;
        case 'pay':
          message.info('跳转到支付页面');
          break;
        default:
          break;
      }
    } catch (error) {
      message.error('操作失败');
    }
  };

  // 查看订单详情
  const showOrderDetail = (order: Order) => {
    setSelectedOrder(order);
    setDetailVisible(true);
  };

  // 表格列定义
  const columns = [
    {
      title: '商品信息',
      dataIndex: 'orderItems',
      key: 'orderItems',
      render: (items: OrderItem[]) => (
        <div>
          {items?.map((item, index) => (
            <div key={index} style={{ display: 'flex', alignItems: 'center', marginBottom: index < items.length - 1 ? '8px' : 0 }}>
              <Image
                width={50}
                height={50}
                src={item.productImage || '/placeholder.jpg'}
                style={{ marginRight: '12px' }}
                fallback="/placeholder.jpg"
              />
              <div>
                <Text strong>{item.productName}</Text>
                <br />
                <Text type="secondary">数量: {item.quantity}</Text>
              </div>
            </div>
          )) || <Text type="secondary">暂无商品信息</Text>}
        </div>
      ),
      width: 300
    },
    {
      title: '订单号',
      dataIndex: 'orderNo',
      key: 'orderNo',
      width: 150
    },
    {
      title: '订单状态',
      dataIndex: 'orderStatus',
      key: 'orderStatus',
      render: (status: OrderStatus) => getOrderStatusTag(status),
      width: 100
    },
    {
      title: '支付状态',
      dataIndex: 'paymentStatus',
      key: 'paymentStatus',
      render: (status: PaymentStatus) => getPaymentStatusTag(status),
      width: 100
    },
    {
      title: '订单金额',
      dataIndex: 'actualAmount',
      key: 'actualAmount',
      render: (amount: number) => <Text strong style={{ color: '#ff4d4f' }}>¥{amount.toFixed(2)}</Text>,
      width: 120
    },
    {
      title: '下单时间',
      dataIndex: 'createdAt',
      key: 'createdAt',
      width: 150
    },
    {
      title: '操作',
      key: 'action',
      render: (_: any, record: Order) => {
        const actions = [];
        
        // 查看详情
        actions.push(
          <Button
            key="detail"
            type="link"
            icon={<EyeOutlined />}
            onClick={() => showOrderDetail(record)}
          >
            详情
          </Button>
        );

        // 根据订单状态显示不同操作
        if (record.orderStatus === OrderStatus.PENDING_PAYMENT) {
          actions.push(
            <Button
              key="pay"
              type="link"
              style={{ color: '#52c41a' }}
              onClick={() => handleOrderAction('pay', record.id)}
            >
              去支付
            </Button>
          );
          actions.push(
            <Button
              key="cancel"
              type="link"
              danger
              onClick={() => handleOrderAction('cancel', record.id)}
            >
              取消订单
            </Button>
          );
        }

        if (record.orderStatus === OrderStatus.SHIPPED) {
          actions.push(
            <Button
              key="confirm"
              type="link"
              onClick={() => handleOrderAction('confirm', record.id)}
            >
              确认收货
            </Button>
          );
        }

        return <Space>{actions}</Space>;
      },
      width: 200
    }
  ];

  // 标签页配置
  const tabItems = [
    { key: 'all', label: '全部订单' },
    { key: 'pending', label: '待付款' },
    { key: 'paid', label: '待发货' },
    { key: 'shipped', label: '待收货' },
    { key: 'completed', label: '已完成' }
  ];

  if (loading) {
    return (
      <div style={{ padding: '50px', textAlign: 'center' }}>
        <Spin size="large" />
      </div>
    );
  }

  return (
    <div style={{ padding: '20px', backgroundColor: '#f5f5f5', minHeight: '100vh' }}>
      <div style={{ maxWidth: '1200px', margin: '0 auto' }}>
        <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', marginBottom: '20px' }}>
          <Title level={2}>我的订单</Title>
          <Button 
            type="primary" 
            icon={<ShoppingCartOutlined />}
            onClick={() => navigate('/cart')}
          >
            去购物车
          </Button>
        </div>

        <Card>
          <Tabs 
            activeKey={activeTab} 
            onChange={setActiveTab}
            items={tabItems}
          />
          
          {filteredOrders.length > 0 ? (
            <Table
              dataSource={filteredOrders}
              columns={columns}
              rowKey="id"
              pagination={{
                pageSize: 10,
                showSizeChanger: true,
                showQuickJumper: true,
                showTotal: (total) => `共 ${total} 条记录`
              }}
            />
          ) : (
            <Empty 
              description="暂无订单数据"
              image={Empty.PRESENTED_IMAGE_SIMPLE}
            />
          )}
        </Card>

        {/* 订单详情弹窗 */}
        <Modal
          title="订单详情"
          open={detailVisible}
          onCancel={() => setDetailVisible(false)}
          footer={null}
          width={800}
        >
          {selectedOrder && (
            <div>
              <Descriptions title="订单信息" bordered column={2}>
                <Descriptions.Item label="订单号">{selectedOrder.orderNo}</Descriptions.Item>
                <Descriptions.Item label="下单时间">{selectedOrder.createdAt}</Descriptions.Item>
                <Descriptions.Item label="订单状态">{getOrderStatusTag(selectedOrder.orderStatus)}</Descriptions.Item>
                <Descriptions.Item label="支付状态">{getPaymentStatusTag(selectedOrder.paymentStatus)}</Descriptions.Item>
                <Descriptions.Item label="支付方式">{selectedOrder.paymentMethod}</Descriptions.Item>
                <Descriptions.Item label="订单金额">¥{selectedOrder.actualAmount.toFixed(2)}</Descriptions.Item>
                {selectedOrder.orderRemark && (
                  <Descriptions.Item label="订单备注" span={2}>{selectedOrder.orderRemark}</Descriptions.Item>
                )}
              </Descriptions>

              <Descriptions title="收货信息" bordered column={1} style={{ marginTop: '20px' }}>
                <Descriptions.Item label="收货人">{selectedOrder.receiverName}</Descriptions.Item>
                <Descriptions.Item label="联系电话">{selectedOrder.receiverPhone}</Descriptions.Item>
                <Descriptions.Item label="收货地址">{selectedOrder.receiverAddress}</Descriptions.Item>
              </Descriptions>

              <div style={{ marginTop: '20px' }}>
                <Title level={4}>商品清单</Title>
                <List
                  dataSource={selectedOrder.orderItems}
                  renderItem={(item: OrderItem) => (
                    <List.Item>
                      <List.Item.Meta
                        avatar={
                          <Image
                            width={60}
                            height={60}
                            src={item.productImage || '/placeholder.jpg'}
                            fallback="/placeholder.jpg"
                          />
                        }
                        title={item.productName}
                        description={
                          <Space>
                            <Text>单价: ¥{item.price.toFixed(2)}</Text>
                            <Text>数量: {item.quantity}</Text>
                            <Text strong>小计: ¥{item.totalPrice.toFixed(2)}</Text>
                          </Space>
                        }
                      />
                    </List.Item>
                  )}
                />
              </div>
            </div>
          )}
        </Modal>
      </div>
    </div>
  );
};

export default OrderPage;
