import React, { useEffect, useState } from 'react';
import { 
  Card, 
  Row, 
  Col, 
  Button, 
  Typography, 
  Image, 
  Tag, 
  InputNumber, 
  Space, 
  Divider,
  Spin,
  Alert,
  Breadcrumb,
  Tabs,
  Rate,
  Radio,
  Carousel,
  List,
  Avatar,
  Descriptions,
  Table,
  Tooltip
} from 'antd';
import { Comment } from '@ant-design/compatible';
import { 
  ShoppingCartOutlined, 
  HeartOutlined, 
  HomeOutlined, 
  ShareAltOutlined,
  LeftOutlined,
  RightOutlined,
  LikeOutlined,
  DislikeOutlined,
  MessageOutlined
} from '@ant-design/icons';
import { useDispatch, useSelector } from 'react-redux';
import { useParams, useNavigate } from 'react-router-dom';
import { RootState, AppDispatch } from '../store';
import { fetchProductById, resetCurrentProduct } from '../store/slices/productSlice';

const { Title, Text, Paragraph } = Typography;
const { TabPane } = Tabs;

interface SpecificationOption {
  name: string;
  options: string[];
}

interface ProductReview {
  id: number;
  username: string;
  avatar: string;
  content: string;
  rating: number;
  createdAt: string;
  images?: string[];
  likes: number;
  dislikes: number;
}

// 模拟商品规格数据
const mockSpecifications: SpecificationOption[] = [
  {
    name: '颜色',
    options: ['黑色', '白色', '蓝色', '红色']
  },
  {
    name: '尺寸',
    options: ['S', 'M', 'L', 'XL', 'XXL']
  },
  {
    name: '版本',
    options: ['标准版', '豪华版', '限定版']
  }
];

// 模拟商品参数数据
const mockParameters = [
  { key: '品牌', value: 'LuckyMall' },
  { key: '产地', value: '中国' },
  { key: '材质', value: '优质面料' },
  { key: '上市时间', value: '2023年' },
  { key: '保修期', value: '12个月' }
];

// 模拟商品评价数据
const mockReviews: ProductReview[] = [
  {
    id: 1,
    username: '用户1',
    avatar: 'https://joeschmoe.io/api/v1/random',
    content: '商品质量非常好，物流很快，包装完好，下次还会购买！',
    rating: 5,
    createdAt: '2023-05-10',
    images: [
      'https://via.placeholder.com/100x100',
      'https://via.placeholder.com/100x100'
    ],
    likes: 12,
    dislikes: 0
  },
  {
    id: 2,
    username: '用户2',
    avatar: 'https://joeschmoe.io/api/v1/random',
    content: '还不错，但是有点小贵。',
    rating: 4,
    createdAt: '2023-05-08',
    likes: 5,
    dislikes: 1
  },
  {
    id: 3,
    username: '用户3',
    avatar: 'https://joeschmoe.io/api/v1/random',
    content: '一般般吧，没有描述的那么好。',
    rating: 3,
    createdAt: '2023-05-05',
    likes: 2,
    dislikes: 3
  }
];

const ProductDetail: React.FC = () => {
  const { id } = useParams<{ id: string }>();
  const navigate = useNavigate();
  const dispatch = useDispatch<AppDispatch>();
  
  const { 
    currentProduct, 
    detailLoading, 
    detailError 
  } = useSelector((state: RootState) => state.products);
  
  const [quantity, setQuantity] = useState(1);
  const [selectedSpecs, setSelectedSpecs] = useState<Record<string, string>>({});
  const [activeTab, setActiveTab] = useState('detail');
  const [carouselRef, setCarouselRef] = useState<any>(null);
  
  // 模拟多张商品图片
  const productImages = currentProduct 
    ? [
        currentProduct.imageUrl || 'https://via.placeholder.com/400x400?text=暂无图片',
        'https://via.placeholder.com/400x400?text=商品图2',
        'https://via.placeholder.com/400x400?text=商品图3',
        'https://via.placeholder.com/400x400?text=商品图4'
      ]
    : [];
  
  useEffect(() => {
    if (id) {
      dispatch(fetchProductById(Number(id)));
    }
    
    // 组件卸载时重置当前商品
    return () => {
      dispatch(resetCurrentProduct());
    };
  }, [dispatch, id]);
  
  const handleQuantityChange = (value: number | null) => {
    if (value && value > 0) {
      setQuantity(value);
    }
  };
  
  const handleSpecChange = (specName: string, value: string) => {
    setSelectedSpecs(prev => ({
      ...prev,
      [specName]: value
    }));
  };
  
  const handleAddToCart = () => {
    if (currentProduct) {
      // 这里暂时只是模拟添加到购物车
      console.log('添加到购物车:', { 
        product: currentProduct, 
        quantity,
        specifications: selectedSpecs
      });
      // TODO: 实际项目中会dispatch addToCart action
    }
  };
  
  const handleBuyNow = () => {
    if (currentProduct) {
      // 这里暂时只是模拟立即购买
      console.log('立即购买:', { 
        product: currentProduct, 
        quantity,
        specifications: selectedSpecs
      });
      // TODO: 实际项目中会跳转到订单页面
    }
  };
  
  // 渲染商品规格选择
  const renderSpecifications = () => (
    <div style={{ marginBottom: 20 }}>
      <Card title="商品规格">
        {mockSpecifications.map((spec) => (
          <div key={spec.name} style={{ marginBottom: 16 }}>
            <Text strong>{spec.name}：</Text>
            <div style={{ marginTop: 8 }}>
              <Radio.Group 
                value={selectedSpecs[spec.name]} 
                onChange={(e) => handleSpecChange(spec.name, e.target.value)}
              >
                <Space wrap>
                  {spec.options.map(option => (
                    <Radio.Button key={option} value={option}>
                      {option}
                    </Radio.Button>
                  ))}
                </Space>
              </Radio.Group>
            </div>
          </div>
        ))}
      </Card>
    </div>
  );
  
  // 渲染商品参数
  const renderParameters = () => (
    <Card title="商品参数" style={{ marginBottom: 20 }}>
      <Descriptions bordered column={1}>
        {mockParameters.map((param, index) => (
          <Descriptions.Item key={index} label={param.key}>
            {param.value}
          </Descriptions.Item>
        ))}
      </Descriptions>
    </Card>
  );
  
  // 渲染商品评价
  const renderReviews = () => (
    <Card title="用户评价" style={{ marginBottom: 20 }}>
      <div style={{ marginBottom: 20 }}>
        <Space align="center" size="large">
          <div style={{ textAlign: 'center' }}>
            <Title level={2} style={{ margin: 0, color: '#ff4d4f' }}>4.8</Title>
            <Rate allowHalf defaultValue={4.8} disabled />
          </div>
          <div>
            <Text>好评率 96%</Text>
            <br />
            <Text type="secondary">共 120 条评价</Text>
          </div>
        </Space>
      </div>
      
      <List
        itemLayout="vertical"
        dataSource={mockReviews}
        renderItem={item => (
          <List.Item
            key={item.id}
            actions={[
              <Tooltip title="有用">
                <Button type="text" icon={<LikeOutlined />}>
                  {item.likes}
                </Button>
              </Tooltip>,
              <Tooltip title="没用">
                <Button type="text" icon={<DislikeOutlined />}>
                  {item.dislikes}
                </Button>
              </Tooltip>,
              <Button type="text" icon={<MessageOutlined />}>
                回复
              </Button>
            ]}
          >
            <Comment
              author={<Text strong>{item.username}</Text>}
              avatar={<Avatar src={item.avatar} />}
              content={
                <>
                  <div style={{ marginBottom: 8 }}>
                    <Rate allowHalf value={item.rating} disabled style={{ fontSize: 14 }} />
                    <Text type="secondary" style={{ marginLeft: 8 }}>
                      {item.createdAt}
                    </Text>
                  </div>
                  <Paragraph>{item.content}</Paragraph>
                  {item.images && (
                    <div style={{ display: 'flex', gap: 8 }}>
                      {item.images.map((img, idx) => (
                        <Image
                          key={idx}
                          src={img}
                          width={80}
                          height={80}
                          style={{ objectFit: 'cover' }}
                        />
                      ))}
                    </div>
                  )}
                </>
              }
            />
          </List.Item>
        )}
      />
    </Card>
  );
  
  if (detailError) {
    return (
      <div style={{ padding: '20px' }}>
        <Alert
          message="加载失败"
          description={detailError}
          type="error"
          showIcon
          action={
            <Space>
              <Button size="small" onClick={() => navigate('/')}>
                返回首页
              </Button>
              <Button size="small" danger onClick={() => id && dispatch(fetchProductById(Number(id)))}>
                重试
              </Button>
            </Space>
          }
        />
      </div>
    );
  }
  
  if (detailLoading || !currentProduct) {
    return (
      <div style={{ padding: '20px', textAlign: 'center' }}>
        <Spin size="large" />
      </div>
    );
  }
  
  return (
    <div style={{ padding: '20px' }}>
      {/* 面包屑导航 */}
      <Breadcrumb style={{ marginBottom: 20 }}>
        <Breadcrumb.Item>
          <HomeOutlined onClick={() => navigate('/')} style={{ cursor: 'pointer' }} />
        </Breadcrumb.Item>
        <Breadcrumb.Item>
          <span onClick={() => navigate('/')} style={{ cursor: 'pointer' }}>
            商品列表
          </span>
        </Breadcrumb.Item>
        {currentProduct.categoryName && (
          <Breadcrumb.Item>{currentProduct.categoryName}</Breadcrumb.Item>
        )}
        <Breadcrumb.Item>{currentProduct.name}</Breadcrumb.Item>
      </Breadcrumb>
      
      <Row gutter={[32, 32]}>
        {/* 商品图片区域 */}
        <Col xs={24} md={12}>
          <Card>
            <div style={{ position: 'relative' }}>
              <Carousel
                ref={ref => setCarouselRef(ref)}
                autoplay
                dots={{ className: 'carousel-dots' }}
              >
                {productImages.map((image, index) => (
                  <div key={index}>
                    <Image
                      width="100%"
                      height={400}
                      src={image}
                      alt={`${currentProduct.name}-${index}`}
                      style={{ objectFit: 'cover' }}
                    />
                  </div>
                ))}
              </Carousel>
              
              {/* 轮播图导航按钮 */}
              <Button 
                icon={<LeftOutlined />} 
                style={{ position: 'absolute', left: 10, top: '50%', transform: 'translateY(-50%)', zIndex: 2 }}
                onClick={() => carouselRef && carouselRef.prev()}
              />
              <Button 
                icon={<RightOutlined />} 
                style={{ position: 'absolute', right: 10, top: '50%', transform: 'translateY(-50%)', zIndex: 2 }}
                onClick={() => carouselRef && carouselRef.next()}
              />
            </div>
            
            {/* 缩略图预览 */}
            <div style={{ display: 'flex', marginTop: 16, gap: 8 }}>
              {productImages.map((image, index) => (
                <div 
                  key={index}
                  style={{ 
                    cursor: 'pointer', 
                    border: '2px solid #f0f0f0',
                    padding: 2,
                    width: 80,
                    height: 80
                  }}
                  onClick={() => carouselRef && carouselRef.goTo(index)}
                >
                  <Image
                    src={image}
                    width={70}
                    height={70}
                    style={{ objectFit: 'cover' }}
                    preview={false}
                  />
                </div>
              ))}
            </div>
          </Card>
        </Col>
        
        {/* 商品信息区域 */}
        <Col xs={24} md={12}>
          <div>
            {/* 商品标题 */}
            <Title level={2}>{currentProduct.name}</Title>
            
            {/* 商品评分 */}
            <div style={{ marginBottom: 16 }}>
              <Rate allowHalf defaultValue={currentProduct.rating || 4.5} disabled />
              <Text type="secondary" style={{ marginLeft: 8 }}>
                ({currentProduct.reviewCount || 0} 评价)
              </Text>
            </div>
            
            {/* 商品标签 */}
            <Space style={{ marginBottom: 16 }}>
              {currentProduct.categoryName && (
                <Tag color="blue">{currentProduct.categoryName}</Tag>
              )}
              {currentProduct.status === 1 ? (
                <Tag color="green">现货</Tag>
              ) : (
                <Tag color="red">缺货</Tag>
              )}
            </Space>
            
            {/* 价格信息 */}
            <Card style={{ marginBottom: 16 }}>
              <Space direction="vertical" size="small">
                <div>
                  <Text type="secondary">价格</Text>
                </div>
                <div>
                  <Text strong style={{ fontSize: 28, color: '#ff4d4f' }}>
                    ¥{currentProduct.price}
                  </Text>
                </div>
              </Space>
            </Card>
            
            {/* 商品规格选择 */}
            {renderSpecifications()}
            
            {/* 库存和销量 */}
            <Row gutter={16} style={{ marginBottom: 16 }}>
              <Col span={12}>
                <Card size="small">
                  <Text type="secondary">库存</Text>
                  <br />
                  <Text strong>{currentProduct.stockQuantity} 件</Text>
                </Card>
              </Col>
              <Col span={12}>
                <Card size="small">
                  <Text type="secondary">销量</Text>
                  <br />
                  <Text strong>{currentProduct.salesCount} 件</Text>
                </Card>
              </Col>
            </Row>
            
            {/* 购买数量选择 */}
            <Card style={{ marginBottom: 20 }}>
              <Space direction="vertical" size="middle" style={{ width: '100%' }}>
                <div>
                  <Text strong>购买数量</Text>
                </div>
                <Space>
                  <InputNumber
                    min={1}
                    max={currentProduct.stockQuantity}
                    value={quantity}
                    onChange={handleQuantityChange}
                    style={{ width: 120 }}
                  />
                  <Text type="secondary">
                    (库存 {currentProduct.stockQuantity} 件)
                  </Text>
                </Space>
              </Space>
            </Card>
            
            {/* 操作按钮 */}
            <Space direction="vertical" size="middle" style={{ width: '100%' }}>
              <Space size="middle">
                <Button 
                  type="primary" 
                  size="large" 
                  danger
                  onClick={handleBuyNow}
                  disabled={currentProduct.stockQuantity === 0}
                  style={{ minWidth: 120 }}
                >
                  立即购买
                </Button>
                <Button 
                  size="large" 
                  icon={<ShoppingCartOutlined />}
                  onClick={handleAddToCart}
                  disabled={currentProduct.stockQuantity === 0}
                  style={{ minWidth: 120 }}
                >
                  加入购物车
                </Button>
                <Button 
                  size="large" 
                  icon={<HeartOutlined />}
                  style={{ minWidth: 100 }}
                >
                  收藏
                </Button>
              </Space>
              
              {currentProduct.stockQuantity === 0 && (
                <Alert
                  message="商品缺货"
                  description="该商品暂时缺货，请关注补货信息"
                  type="warning"
                  showIcon
                />
              )}
            </Space>
          </div>
        </Col>
      </Row>
      
      <Divider />
      
      {/* 商品详情内容 */}
      <Tabs defaultActiveKey="detail" onChange={setActiveTab}>
        <TabPane tab="商品详情" key="detail">
          <Card>
            {currentProduct.description ? (
              <Paragraph>
                {currentProduct.description}
              </Paragraph>
            ) : (
              <Text type="secondary">暂无商品详情描述</Text>
            )}
          </Card>
        </TabPane>
        <TabPane tab="规格参数" key="params">
          {renderParameters()}
        </TabPane>
        <TabPane tab="用户评价" key="reviews">
          {renderReviews()}
        </TabPane>
      </Tabs>
    </div>
  );
};

export default ProductDetail;
