import React, { useEffect } from 'react';
import { 
  Card, 
  Row, 
  Col, 
  Button, 
  Typography, 
  Image, 
  Tag, 
  InputNumber, 
  Space, 
  Divider,
  Spin,
  Alert,
  Breadcrumb,
  message
} from 'antd';
import { ShoppingCartOutlined, HeartOutlined, HomeOutlined, BugOutlined } from '@ant-design/icons';
import { useDispatch, useSelector } from 'react-redux';
import { useParams, useNavigate } from 'react-router-dom';
import { RootState, AppDispatch } from '../store';
import { fetchProductById, resetCurrentProduct } from '../store/slices/productSlice';
import { addToCartAsync } from '../store/slices/cartSlice';
import { ProductService } from '../services/productService';
import api from '../services/api';

const { Title, Text, Paragraph } = Typography;

const ProductDetail: React.FC = () => {
  const { id } = useParams<{ id: string }>();
  const navigate = useNavigate();
  const dispatch = useDispatch<AppDispatch>();
  
  const { 
    currentProduct, 
    detailLoading, 
    detailError 
  } = useSelector((state: RootState) => state.products);
  
  const [quantity, setQuantity] = React.useState(1);
  const [addingToCart, setAddingToCart] = React.useState(false);
  const [debugInfo, setDebugInfo] = React.useState<string>('');
  
  useEffect(() => {
    console.log("ProductDetail组件加载，ID:", id);
    if (id) {
      // 尝试使用数值ID
      try {
        const numId = Number(id);
        if (!isNaN(numId)) {
          console.log("尝试获取商品详情，数值ID:", numId);
          dispatch(fetchProductById(numId));
        } else {
          console.error("商品ID不是有效的数字:", id);
        }
      } catch (error) {
        console.error("处理商品ID时出错:", error);
      }
    }
    
    // 组件卸载时重置当前商品
    return () => {
      dispatch(resetCurrentProduct());
    };
  }, [dispatch, id]);
  
  const handleQuantityChange = (value: number | null) => {
    if (value && value > 0) {
      setQuantity(value);
    }
  };
  
  const handleAddToCart = async () => {
    if (currentProduct) {
      try {
        setAddingToCart(true);
        // 调用异步action
        await dispatch(addToCartAsync({ product: currentProduct, quantity })).unwrap();
        message.success(`已将${quantity}件"${currentProduct.name}"添加到购物车`);
      } catch (error) {
        message.error('添加到购物车失败，请稍后重试');
        console.error('添加到购物车失败:', error);
      } finally {
        setAddingToCart(false);
      }
    }
  };
  
  const handleBuyNow = async () => {
    if (currentProduct) {
      try {
        setAddingToCart(true);
        // 调用异步action
        await dispatch(addToCartAsync({ product: currentProduct, quantity })).unwrap();
        // 然后跳转到购物车页面
        navigate('/cart');
      } catch (error) {
        message.error('添加到购物车失败，请稍后重试');
        console.error('添加到购物车失败:', error);
      } finally {
        setAddingToCart(false);
      }
    }
  };
  
  // 调试函数：直接测试API
  const handleDebugTest = async () => {
    if (!id) return;
    
    try {
      setDebugInfo('正在测试API...');
      // 直接使用axios测试
      const response = await api.get(`/api/products/test/${id}`);
      setDebugInfo(JSON.stringify(response.data, null, 2));
    } catch (error) {
      console.error('API测试失败:', error);
      setDebugInfo(`API测试失败: ${error instanceof Error ? error.message : String(error)}`);
    }
  };
  
  // 使用模拟数据
  const handleUseMockData = () => {
    if (!id) return;
    
    // 创建一个模拟商品数据
    const mockProduct = {
      id: Number(id),
      name: `模拟商品 ${id}`,
      description: `这是一个模拟商品，用于测试商品详情页面的显示效果。商品ID为${id}。\n\n该商品包含多种功能和特性，适合各种场景使用。\n\n- 高品质材料\n- 精细做工\n- 耐用可靠\n- 多种颜色可选`,
      price: 99.99,
      stockQuantity: 100,
      categoryId: 1,
      categoryName: "模拟分类",
      imageUrl: `https://via.placeholder.com/400x400?text=模拟商品${id}`,
      salesCount: 50,
      status: 1,
      createdAt: new Date().toISOString(),
      updatedAt: new Date().toISOString()
    };
    
    // 使用Redux action设置当前商品
    dispatch({
      type: 'products/fetchProductById/fulfilled',
      payload: mockProduct
    });
    
    // 显示成功消息
    message.success('已加载模拟商品数据');
  };
  
  // 如果有错误，显示错误信息
  if (detailError) {
    return (
      <div style={{ padding: '20px' }}>
        <Alert
          message="加载失败"
          description={detailError}
          type="error"
          showIcon
          action={
            <Space>
              <Button size="small" onClick={() => navigate('/')}>
                返回首页
              </Button>
              <Button size="small" danger onClick={() => id && dispatch(fetchProductById(Number(id)))}>
                重试
              </Button>
              <Button 
                size="small" 
                type="dashed" 
                icon={<BugOutlined />} 
                onClick={handleDebugTest}
              >
                调试
              </Button>
              <Button 
                size="small" 
                type="primary" 
                onClick={handleUseMockData}
              >
                使用模拟数据
              </Button>
            </Space>
          }
        />
        {debugInfo && (
          <div style={{ marginTop: 16, padding: 16, background: '#f5f5f5', borderRadius: 4 }}>
            <Text code>{debugInfo}</Text>
          </div>
        )}
      </div>
    );
  }
  
  // 如果正在加载或没有商品数据，显示加载状态
  if (detailLoading || !currentProduct) {
    return (
      <div style={{ padding: '20px', textAlign: 'center' }}>
        <Spin size="large" />
        <div style={{ marginTop: 16 }}>
          <Text type="secondary">正在加载商品详情...</Text>
        </div>
        <div style={{ marginTop: 16 }}>
          <Button 
            type="dashed" 
            icon={<BugOutlined />} 
            onClick={handleDebugTest}
          >
            调试API
          </Button>
          <Button 
            size="small" 
            type="primary" 
            onClick={handleUseMockData}
          >
            使用模拟数据
          </Button>
        </div>
        {debugInfo && (
          <div style={{ marginTop: 16, padding: 16, background: '#f5f5f5', borderRadius: 4, textAlign: 'left' }}>
            <Text code>{debugInfo}</Text>
          </div>
        )}
      </div>
    );
  }
  
  // 渲染商品详情
  return (
    <div style={{ padding: '20px' }}>
      {/* 面包屑导航 */}
      <Breadcrumb style={{ marginBottom: 20 }}>
        <Breadcrumb.Item>
          <HomeOutlined onClick={() => navigate('/')} style={{ cursor: 'pointer' }} />
        </Breadcrumb.Item>
        <Breadcrumb.Item>
          <span onClick={() => navigate('/products')} style={{ cursor: 'pointer' }}>
            商品列表
          </span>
        </Breadcrumb.Item>
        {currentProduct.categoryName && (
          <Breadcrumb.Item>{currentProduct.categoryName}</Breadcrumb.Item>
        )}
        <Breadcrumb.Item>{currentProduct.name}</Breadcrumb.Item>
      </Breadcrumb>
      
      <Row gutter={[32, 32]}>
        {/* 商品图片区域 */}
        <Col xs={24} md={12}>
          <Card>
            <Image
              width="100%"
              height={400}
              src={currentProduct.imageUrl || 'https://via.placeholder.com/400x400?text=暂无图片'}
              alt={currentProduct.name}
              style={{ objectFit: 'cover' }}
            />
          </Card>
        </Col>
        
        {/* 商品信息区域 */}
        <Col xs={24} md={12}>
          <div>
            {/* 商品标题 */}
            <Title level={2}>{currentProduct.name}</Title>
            
            {/* 商品标签 */}
            <Space style={{ marginBottom: 16 }}>
              {currentProduct.categoryName && (
                <Tag color="blue">{currentProduct.categoryName}</Tag>
              )}
              {currentProduct.status === 1 ? (
                <Tag color="green">现货</Tag>
              ) : (
                <Tag color="red">缺货</Tag>
              )}
            </Space>
            
            {/* 价格信息 */}
            <Card style={{ marginBottom: 16 }}>
              <Space direction="vertical" size="small">
                <div>
                  <Text type="secondary">价格</Text>
                </div>
                <div>
                  <Text strong style={{ fontSize: 28, color: '#ff4d4f' }}>
                    ¥{currentProduct.price}
                  </Text>
                </div>
              </Space>
            </Card>
            
            {/* 库存和销量 */}
            <Row gutter={16} style={{ marginBottom: 16 }}>
              <Col span={12}>
                <Card size="small">
                  <Text type="secondary">库存</Text>
                  <br />
                  <Text strong>{currentProduct.stockQuantity} 件</Text>
                </Card>
              </Col>
              <Col span={12}>
                <Card size="small">
                  <Text type="secondary">销量</Text>
                  <br />
                  <Text strong>{currentProduct.salesCount} 件</Text>
                </Card>
              </Col>
            </Row>
            
            {/* 购买数量选择 */}
            <Card style={{ marginBottom: 20 }}>
              <Space direction="vertical" size="middle" style={{ width: '100%' }}>
                <div>
                  <Text strong>购买数量</Text>
                </div>
                <Space>
                  <InputNumber
                    min={1}
                    max={currentProduct.stockQuantity}
                    value={quantity}
                    onChange={handleQuantityChange}
                    style={{ width: 120 }}
                  />
                  <Text type="secondary">
                    (库存 {currentProduct.stockQuantity} 件)
                  </Text>
                </Space>
              </Space>
            </Card>
            
            {/* 操作按钮 */}
            <Space direction="vertical" size="middle" style={{ width: '100%' }}>
              <Space size="middle">
                <Button 
                  type="primary" 
                  size="large" 
                  danger
                  onClick={handleBuyNow}
                  disabled={currentProduct.stockQuantity === 0 || addingToCart}
                  style={{ minWidth: 120 }}
                >
                  立即购买
                </Button>
                <Button 
                  size="large" 
                  icon={<ShoppingCartOutlined />}
                  onClick={handleAddToCart}
                  disabled={currentProduct.stockQuantity === 0 || addingToCart}
                  style={{ minWidth: 120 }}
                >
                  加入购物车
                </Button>
                <Button 
                  size="large" 
                  icon={<HeartOutlined />}
                  style={{ minWidth: 100 }}
                >
                  收藏
                </Button>
              </Space>
              
              {currentProduct.stockQuantity === 0 && (
                <Alert
                  message="商品缺货"
                  description="该商品暂时缺货，请关注补货信息"
                  type="warning"
                  showIcon
                />
              )}
            </Space>
          </div>
        </Col>
      </Row>
      
      <Divider />
      
      {/* 商品详情描述 */}
      <Row>
        <Col span={24}>
          <Card title="商品详情">
            {currentProduct.description ? (
              <Paragraph>
                {currentProduct.description}
              </Paragraph>
            ) : (
              <Text type="secondary">暂无商品详情描述</Text>
            )}
          </Card>
        </Col>
      </Row>
      
      <Divider />
      
      {/* 用户评价区域（模拟展示） */}
      <Row>
        <Col span={24}>
          <Card title="用户评价">
            <div style={{ textAlign: 'center', padding: '40px 0' }}>
              <Text type="secondary">暂无用户评价</Text>
            </div>
          </Card>
        </Col>
      </Row>
    </div>
  );
};

export default ProductDetail;
