import api from './api';
import { 
  CartItem, 
  ApiResponse, 
  AddToCartRequest, 
  UpdateCartRequest 
} from '../types';

/**
 * 购物车服务类
 */
export class CartService {
  
  /**
   * 获取用户购物车商品列表
   */
  static async getCartItems(userId: number): Promise<CartItem[]> {
    const response = await api.get<ApiResponse<CartItem[]>>(`/api/cart/${userId}`);
    return response.data.data;
  }

  /**
   * 添加商品到购物车
   */
  static async addToCart(userId: number, request: AddToCartRequest): Promise<string> {
    const response = await api.post<ApiResponse<string>>(`/api/cart/${userId}/add`, request);
    return response.data.data;
  }

  /**
   * 更新购物车商品数量
   */
  static async updateCartItem(userId: number, request: UpdateCartRequest): Promise<string> {
    const response = await api.put<ApiResponse<string>>(`/api/cart/${userId}/update`, request);
    return response.data.data;
  }

  /**
   * 从购物车删除指定商品项
   */
  static async removeCartItem(userId: number, cartItemId: number): Promise<string> {
    const response = await api.delete<ApiResponse<string>>(`/api/cart/${userId}/remove/${cartItemId}`);
    return response.data.data;
  }

  /**
   * 从购物车删除指定商品的所有项
   */
  static async removeProductFromCart(userId: number, productId: number): Promise<string> {
    const response = await api.delete<ApiResponse<string>>(`/api/cart/${userId}/remove-product/${productId}`);
    return response.data.data;
  }

  /**
   * 清空用户购物车
   */
  static async clearCart(userId: number): Promise<string> {
    const response = await api.delete<ApiResponse<string>>(`/api/cart/${userId}/clear`);
    return response.data.data;
  }

  /**
   * 获取购物车商品数量
   */
  static async getCartCount(userId: number): Promise<number> {
    const response = await api.get<ApiResponse<number>>(`/api/cart/${userId}/count`);
    return response.data.data;
  }
}
