import api from './api';
import {
  Order,
  CreateOrderRequest,
  CreateOrderResponse,
  InstallmentPlan,
  UserAddress,
  AddAddressRequest,
  ApiResponse,
  PageResponse
} from '../types/order';

// 订单API服务
export const orderApi = {
  // 创建订单
  createOrder: (userId: number, data: CreateOrderRequest): Promise<ApiResponse<CreateOrderResponse>> => {
    return api.post(`/api/orders/${userId}`, data);
  },

  // 获取订单详情
  getOrderById: (orderId: number): Promise<ApiResponse<Order>> => {
    return api.get(`/api/orders/${orderId}`);
  },

  // 获取用户订单列表
  getUserOrders: (userId: number, params?: {
    page?: number;
    size?: number;
    status?: string;
  }): Promise<ApiResponse<Order[]>> => {
    return api.get(`/api/orders/user/${userId}`, { params });
  },

  // 取消订单
  cancelOrder: (orderId: number): Promise<ApiResponse<void>> => {
    return api.put(`/api/orders/${orderId}/cancel`);
  },

  // 确认收货
  confirmReceived: (orderId: number): Promise<ApiResponse<void>> => {
    return api.put(`/api/orders/${orderId}/confirm`);
  }
};

// 分期方案API服务
export const installmentApi = {
  // 获取所有分期方案
  getAllPlans: (): Promise<ApiResponse<InstallmentPlan[]>> => {
    return api.get('/api/orders/installment-plans');
  },

  // 根据金额获取可用分期方案
  getPlansByAmount: (amount: number): Promise<ApiResponse<InstallmentPlan[]>> => {
    return api.get(`/api/orders/installment-plans/available?amount=${amount}`);
  }
};

// 地址API服务
export const addressApi = {
  // 获取用户地址列表
  getUserAddresses: (): Promise<ApiResponse<UserAddress[]>> => {
    return api.get('/api/addresses/my');
  },

  // 添加地址
  addAddress: (data: AddAddressRequest): Promise<ApiResponse<UserAddress>> => {
    return api.post('/api/addresses', data);
  },

  // 更新地址
  updateAddress: (addressId: number, data: Partial<AddAddressRequest>): Promise<ApiResponse<UserAddress>> => {
    return api.put(`/api/addresses/${addressId}`, data);
  },

  // 删除地址
  deleteAddress: (addressId: number): Promise<ApiResponse<void>> => {
    return api.delete(`/api/addresses/${addressId}`);
  },

  // 设置默认地址
  setDefaultAddress: (addressId: number): Promise<ApiResponse<void>> => {
    return api.put(`/api/addresses/${addressId}/default`);
  },

  // 获取地址详情
  getAddressById: (addressId: number): Promise<ApiResponse<UserAddress>> => {
    return api.get(`/api/addresses/${addressId}`);
  }
};
