import React, { useState, useEffect } from 'react';
import { 
  Layout, 
  Carousel, 
  Card, 
  Row, 
  Col, 
  Typography, 
  Button, 
  Tabs, 
  List, 
  Image, 
  Badge, 
  Space, 
  Divider,
  Skeleton
} from 'antd';
import { 
  FireOutlined, 
  GiftOutlined, 
  RocketOutlined, 
  ClockCircleOutlined, 
  TagOutlined,
  RightOutlined,
  ShoppingCartOutlined
} from '@ant-design/icons';
import { useNavigate } from 'react-router-dom';
import { useDispatch } from 'react-redux';
import { AppDispatch } from '../store';
import { addToCartAsync } from '../store/slices/cartSlice';
import { Product } from '../types';

// 导入模拟产品数据
import { mockProducts } from '../services/productService';

const { Content } = Layout;
const { Title, Text } = Typography;
const { TabPane } = Tabs;

// 模拟轮播图数据
const carouselImages = [
  'https://picsum.photos/seed/electronics1/1180/400',
  'https://picsum.photos/seed/smartphone/1180/400',
  'https://picsum.photos/seed/appliances/1180/400',
  'https://picsum.photos/seed/computers/1180/400',
];

// 模拟分类数据
const categories = [
  { id: 1, name: '手机数码', icon: '📱' },
  { id: 2, name: '电脑办公', icon: '💻' },
  { id: 3, name: '家用电器', icon: '🔌' },
  { id: 4, name: '美妆护肤', icon: '💄' },
  { id: 5, name: '服饰鞋包', icon: '👔' },
  { id: 6, name: '食品生鲜', icon: '🍎' },
  { id: 7, name: '图书音像', icon: '📚' },
  { id: 8, name: '母婴玩具', icon: '🧸' },
  { id: 9, name: '家居家装', icon: '🛋️' },
  { id: 10, name: '运动户外', icon: '⚽' },
];

// 秒杀商品使用模拟数据中的前4个商品
const flashSaleProducts = mockProducts.slice(0, 4).map(product => ({
  id: product.id,
  name: product.name,
  price: product.price,
  originalPrice: Math.round(product.price * 1.1), // 原价为当前价格的1.1倍
  imageUrl: product.imageUrl,
  discount: Math.round((product.price / (product.price * 1.1)) * 10 * 10) / 10, // 计算折扣
  sold: Math.round(Math.random() * 50) + 50, // 随机销售量，50-100之间
  stock: 100
}));

// 推荐商品使用模拟数据
const recommendProducts = mockProducts;

// 模拟热门品牌数据
const hotBrands = [
  { id: 1, name: 'Apple', logo: 'https://picsum.photos/seed/apple/80/40' },
  { id: 2, name: '华为', logo: 'https://picsum.photos/seed/huawei/80/40' },
  { id: 3, name: '小米', logo: 'https://picsum.photos/seed/xiaomi/80/40' },
  { id: 4, name: '三星', logo: 'https://picsum.photos/seed/samsung/80/40' },
  { id: 5, name: '戴森', logo: 'https://picsum.photos/seed/dyson/80/40' },
  { id: 6, name: '索尼', logo: 'https://picsum.photos/seed/sony/80/40' },
];

// 模拟倒计时
const CountdownTimer: React.FC = () => {
  const [hours, setHours] = useState(1);
  const [minutes, setMinutes] = useState(59);
  const [seconds, setSeconds] = useState(59);

  useEffect(() => {
    const timer = setInterval(() => {
      if (seconds > 0) {
        setSeconds(seconds - 1);
      } else if (minutes > 0) {
        setMinutes(minutes - 1);
        setSeconds(59);
      } else if (hours > 0) {
        setHours(hours - 1);
        setMinutes(59);
        setSeconds(59);
      } else {
        clearInterval(timer);
      }
    }, 1000);

    return () => clearInterval(timer);
  }, [hours, minutes, seconds]);

  return (
    <Space>
      <ClockCircleOutlined />
      <Text strong>
        {String(hours).padStart(2, '0')}:{String(minutes).padStart(2, '0')}:{String(seconds).padStart(2, '0')}
      </Text>
    </Space>
  );
};

const HomePage: React.FC = () => {
  const navigate = useNavigate();
  const dispatch = useDispatch<AppDispatch>();
  const [loading, setLoading] = useState(true);

  // 模拟数据加载
  useEffect(() => {
    const timer = setTimeout(() => {
      setLoading(false);
    }, 1000);

    return () => clearTimeout(timer);
  }, []);

  // 处理商品点击
  const handleProductClick = (productId: number) => {
    navigate(`/product/${productId}`);
  };

  // 处理添加到购物车
  const handleAddToCart = (product: Product, event: React.MouseEvent) => {
    event.stopPropagation();
    dispatch(addToCartAsync({ product, quantity: 1 }));
  };

  // 处理分类点击
  const handleCategoryClick = (categoryId: number) => {
    navigate(`/category/${categoryId}`);
  };

  return (
    <Content style={{ padding: '0 50px', marginTop: 20 }}>
      <Row gutter={[16, 16]}>
        {/* 左侧分类导航 */}
        <Col xs={0} sm={0} md={4} lg={4} xl={4}>
          <Card style={{ marginBottom: 16 }}>
            <Title level={5}>商品分类</Title>
            <List
              dataSource={categories}
              renderItem={item => (
                <List.Item 
                  onClick={() => handleCategoryClick(item.id)}
                  style={{ cursor: 'pointer', padding: '8px 0' }}
                >
                  <Space>
                    <span style={{ fontSize: 18 }}>{item.icon}</span>
                    <span>{item.name}</span>
                  </Space>
                </List.Item>
              )}
            />
          </Card>
        </Col>

        {/* 中间内容区域 */}
        <Col xs={24} sm={24} md={16} lg={16} xl={16}>
          {/* 轮播图 */}
          <Carousel autoplay style={{ marginBottom: 16 }}>
            {carouselImages.map((image, index) => (
              <div key={index}>
                <img 
                  src={image} 
                  alt={`Banner ${index + 1}`} 
                  style={{ width: '100%', height: 'auto', maxHeight: 400, objectFit: 'cover' }}
                />
              </div>
            ))}
          </Carousel>

          {/* 秒杀专区 */}
          <Card 
            title={
              <Space>
                <FireOutlined style={{ color: '#ff4d4f' }} />
                <span>限时秒杀</span>
                <CountdownTimer />
              </Space>
            }
            extra={<Button type="link">更多秒杀 <RightOutlined /></Button>}
            style={{ marginBottom: 16 }}
          >
            <Row gutter={[16, 16]}>
              {flashSaleProducts.map(product => (
                <Col xs={12} sm={12} md={6} lg={6} xl={6} key={product.id}>
                  <Card
                    hoverable
                    cover={
                      <div style={{ position: 'relative', padding: '8px' }}>
                        <Badge.Ribbon text={`${product.discount}折`} color="red">
                          <img 
                            alt={product.name} 
                            src={product.imageUrl}
                            style={{ width: '100%', height: 'auto' }}
                          />
                        </Badge.Ribbon>
                      </div>
                    }
                    bodyStyle={{ padding: '12px' }}
                    onClick={() => handleProductClick(product.id)}
                  >
                    <Card.Meta
                      title={product.name}
                      description={
                        <div>
                          <div>
                            <Text type="danger" strong>¥{product.price}</Text>
                            <Text delete type="secondary" style={{ marginLeft: 8 }}>¥{product.originalPrice}</Text>
                          </div>
                          <div style={{ marginTop: 8 }}>
                            <div style={{ backgroundColor: '#f5f5f5', borderRadius: 10, height: 10, width: '100%' }}>
                              <div 
                                style={{ 
                                  backgroundColor: '#ff4d4f', 
                                  borderRadius: 10, 
                                  height: 10, 
                                  width: `${(product.sold / product.stock) * 100}%` 
                                }}
                              />
                            </div>
                            <div style={{ marginTop: 4 }}>
                              <Text type="secondary" style={{ fontSize: 12 }}>已售{product.sold}%</Text>
                            </div>
                          </div>
                        </div>
                      }
                    />
                  </Card>
                </Col>
              ))}
            </Row>
          </Card>

          {/* 热门品牌 */}
          <Card 
            title={
              <Space>
                <TagOutlined />
                <span>热门品牌</span>
              </Space>
            }
            style={{ marginBottom: 16 }}
          >
            <Row gutter={[16, 16]}>
              {hotBrands.map(brand => (
                <Col xs={8} sm={8} md={4} lg={4} xl={4} key={brand.id}>
                  <Card
                    hoverable
                    bodyStyle={{ padding: 0, textAlign: 'center' }}
                  >
                    <img 
                      alt={brand.name} 
                      src={brand.logo}
                      style={{ width: '100%', height: 'auto' }}
                    />
                    <div style={{ padding: '8px 0' }}>{brand.name}</div>
                  </Card>
                </Col>
              ))}
            </Row>
          </Card>

          {/* 商品推荐 */}
          <Card 
            title={
              <Space>
                <RocketOutlined />
                <span>商品推荐</span>
              </Space>
            }
            style={{ marginBottom: 16 }}
          >
            <Tabs defaultActiveKey="1">
              <TabPane tab="热门推荐" key="1">
                <Row gutter={[16, 16]}>
                  {loading ? (
                    Array(8).fill(null).map((_, index) => (
                      <Col xs={12} sm={12} md={6} lg={6} xl={6} key={index}>
                        <Card>
                          <Skeleton.Image style={{ width: '100%', height: 150 }} />
                          <Skeleton active paragraph={{ rows: 2 }} />
                        </Card>
                      </Col>
                    ))
                  ) : (
                    recommendProducts.map(product => (
                      <Col xs={12} sm={12} md={6} lg={6} xl={6} key={product.id}>
                        <Card
                          hoverable
                          cover={
                            <img 
                              alt={product.name} 
                              src={product.imageUrl}
                              style={{ width: '100%', height: 'auto', padding: '8px' }}
                            />
                          }
                          bodyStyle={{ padding: '12px' }}
                          onClick={() => handleProductClick(product.id)}
                          actions={[
                            <Button 
                              type="primary" 
                              icon={<ShoppingCartOutlined />} 
                              size="small"
                              onClick={(e) => handleAddToCart(product, e)}
                            >
                              加入购物车
                            </Button>
                          ]}
                        >
                          <Card.Meta
                            title={
                              <Text ellipsis style={{ width: '100%' }}>
                                {product.name}
                              </Text>
                            }
                            description={
                              <div>
                                <Text type="secondary" ellipsis style={{ width: '100%', display: 'block' }}>
                                  {product.description}
                                </Text>
                                <Text type="danger" strong style={{ marginTop: 8, display: 'block' }}>
                                  ¥{product.price}
                                </Text>
                              </div>
                            }
                          />
                        </Card>
                      </Col>
                    ))
                  )}
                </Row>
              </TabPane>
              <TabPane tab="新品上市" key="2">
                <div style={{ padding: '20px 0', textAlign: 'center' }}>
                  更多新品即将上线，敬请期待！
                </div>
              </TabPane>
              <TabPane tab="特惠专区" key="3">
                <div style={{ padding: '20px 0', textAlign: 'center' }}>
                  特惠商品正在准备中，敬请期待！
                </div>
              </TabPane>
            </Tabs>
          </Card>
        </Col>

        {/* 右侧信息区域 */}
        <Col xs={0} sm={0} md={4} lg={4} xl={4}>
          <Card style={{ marginBottom: 16 }}>
            <div style={{ textAlign: 'center', padding: '10px 0' }}>
              <img 
                src="https://via.placeholder.com/80x80?text=Avatar" 
                alt="User Avatar" 
                style={{ borderRadius: '50%', width: 60, height: 60 }}
              />
              <div style={{ marginTop: 10 }}>
                <Text>您好，欢迎来到招财商城</Text>
              </div>
              <div style={{ marginTop: 10 }}>
                <Space>
                  <Button type="primary" size="small">登录</Button>
                  <Button size="small">注册</Button>
                </Space>
              </div>
            </div>
          </Card>

          <Card 
            title="公告" 
            style={{ marginBottom: 16 }}
            bodyStyle={{ padding: '10px' }}
          >
            <List
              size="small"
              dataSource={[
                '招财商城618大促即将开始',
                '新用户注册送100元优惠券',
                '下载APP享受更多优惠',
                '每日签到领取积分'
              ]}
              renderItem={item => (
                <List.Item style={{ padding: '8px 0' }}>
                  <Text ellipsis>{item}</Text>
                </List.Item>
              )}
            />
          </Card>

          <Card title="热卖榜单">
            <List
              size="small"
              dataSource={recommendProducts.slice(0, 5)}
              renderItem={(item, index) => (
                <List.Item 
                  style={{ padding: '8px 0', cursor: 'pointer' }}
                  onClick={() => handleProductClick(item.id)}
                >
                  <List.Item.Meta
                    avatar={
                      <div style={{ 
                        width: 18, 
                        height: 18, 
                        backgroundColor: index < 3 ? '#ff4d4f' : '#f5f5f5', 
                        color: index < 3 ? 'white' : 'rgba(0,0,0,0.65)',
                        textAlign: 'center',
                        lineHeight: '18px',
                        borderRadius: 2
                      }}>
                        {index + 1}
                      </div>
                    }
                    title={
                      <Text ellipsis style={{ width: '100%' }}>
                        {item.name}
                      </Text>
                    }
                  />
                </List.Item>
              )}
            />
          </Card>
        </Col>
      </Row>
    </Content>
  );
};

export default HomePage;
