import React, { useState, useEffect } from 'react';
import { 
  Card, 
  Form, 
  Button, 
  Radio, 
  Input, 
  Space, 
  Divider, 
  message, 
  Row,
  Col,
  Typography,
  Spin
} from 'antd';
import { useNavigate, useLocation } from 'react-router-dom';
import { EnvironmentOutlined } from '@ant-design/icons';
import { PaymentMethod, InstallmentPlan, CreateOrderRequest } from '../types/order';
import { CartItem } from '../types';
import { installmentApi, orderApi } from '../services/orderApi';

const { Title, Text } = Typography;
const { TextArea } = Input;

const CheckoutPage: React.FC = () => {
  const navigate = useNavigate();
  const location = useLocation();
  const { selectedItems } = location.state || { selectedItems: [] };
  
  const [form] = Form.useForm();
  const [loading, setLoading] = useState(false);
  const [submitting, setSubmitting] = useState(false);
  const [selectedInstallment, setSelectedInstallment] = useState<number | null>(null);
  const [paymentMethod, setPaymentMethod] = useState<PaymentMethod | null>(null);
  const [showInstallmentOptions, setShowInstallmentOptions] = useState(false);

  // 计算总金额
  const totalAmount = selectedItems.reduce((sum: number, item: CartItem) => 
    sum + item.product.price * item.quantity, 0
  );
  const shippingFee = 0; // 假设免运费
  const finalAmount = totalAmount + shippingFee;

  // 使用实际金额，如果没有商品则使用测试金额
  const actualAmount = finalAmount > 0 ? finalAmount : 5999.00;

  // 直接初始化分期方案数据
  const [installmentPlans, setInstallmentPlans] = useState<InstallmentPlan[]>([
    {
      id: 1,
      planName: '3期免息',
      installmentCount: 3,
      interestRate: 0,
      minAmount: 0,
      maxAmount: 100000,
      status: 1
    },
    {
      id: 2,
      planName: '6期免息',
      installmentCount: 6,
      interestRate: 0,
      minAmount: 0,
      maxAmount: 100000,
      status: 1
    },
    {
      id: 3,
      planName: '12期免息',
      installmentCount: 12,
      interestRate: 0,
      minAmount: 0,
      maxAmount: 100000,
      status: 1
    },
    {
      id: 4,
      planName: '24期免息',
      installmentCount: 24,
      interestRate: 0,
      minAmount: 0,
      maxAmount: 100000,
      status: 1
    }
  ]);

  console.log('订单金额调试信息:', {
    selectedItems,
    totalAmount,
    finalAmount,
    actualAmount,
    installmentPlansCount: installmentPlans.length
  });

  useEffect(() => {
    if (!selectedItems || selectedItems.length === 0) {
      message.warning('未检测到商品，使用测试金额演示分期功能');
      // 不直接跳转，允许用户测试分期功能
    }
    // 可选择性加载后端数据，但不依赖它
    loadInstallmentPlans();
  }, [selectedItems, navigate]);

  // 加载分期方案（可选）
  const loadInstallmentPlans = async () => {
    try {
      const response = await installmentApi.getPlansByAmount(actualAmount);
      if (response.code === 200 && response.data && response.data.length > 0) {
        setInstallmentPlans(response.data);
        console.log('从后端加载分期方案成功:', response.data);
      }
    } catch (error) {
      console.log('使用本地分期方案数据，后端加载失败:', error);
      // 保持使用本地初始化的数据
    }
  };

  // 计算月供
  const calculateMonthlyPayment = (plan: InstallmentPlan) => {
    const amount = actualAmount;
    if (plan.interestRate === 0) {
      return amount / plan.installmentCount;
    }
    // 简化的有息计算
    const monthlyRate = plan.interestRate / 100;
    const totalWithInterest = amount * (1 + monthlyRate * plan.installmentCount);
    return totalWithInterest / plan.installmentCount;
  };

  // 处理支付方式选择
  const handlePaymentMethodChange = (method: PaymentMethod) => {
    setPaymentMethod(method);
    // 如果选择信用卡，显示分期选项；否则隐藏
    if (method === PaymentMethod.CREDIT_CARD) {
      setShowInstallmentOptions(true);
    } else {
      setShowInstallmentOptions(false);
      setSelectedInstallment(null);
    }
  };

  // 提交订单
  const handleSubmit = async (values: any) => {
    // 验证支付方式是否选择
    if (!paymentMethod) {
      message.error('请选择支付方式');
      return;
    }

    // 验证收货地址是否选择
    if (!values.addressId) {
      message.error('请选择收货地址');
      return;
    }

    try {
      setSubmitting(true);
      
      // 构建订单创建请求
      const orderRequest: CreateOrderRequest = {
        cartItemIds: selectedItems.map((item: CartItem) => item.id),
        addressId: values.addressId,
        paymentMethod: paymentMethod,
        isInstallment: !!selectedInstallment,
        installmentPlanId: selectedInstallment || undefined,
        orderRemark: values.orderRemark || '',
        expectedAmount: actualAmount
      };
      
      console.log('创建订单请求:', orderRequest);
      
      // 调用真实的订单创建API
      const response = await orderApi.createOrder(orderRequest);
      
      if (response.success) {
        message.success('订单创建成功！');
        console.log('订单创建成功:', response.data);
        
        // 跳转到订单页面
        navigate('/orders');
      } else {
        message.error(response.message || '创建订单失败');
      }
      
    } catch (error: any) {
      console.error('创建订单失败:', error);
      const errorMessage = error.response?.data?.message || error.message || '创建订单失败，请稍后重试';
      message.error(errorMessage);
    } finally {
      setSubmitting(false);
    }
  };

  // 渲染支付方式选择组件
  const renderPaymentMethods = () => {
    return (
      <Card title="支付方式" style={{ marginBottom: '16px' }}>
        <div style={{ display: 'grid', gridTemplateColumns: '1fr 1fr', gap: '12px' }}>
          <Button
            size="large"
            style={{
              height: '60px',
              display: 'flex',
              flexDirection: 'column',
              justifyContent: 'center',
              alignItems: 'center',
              backgroundColor: paymentMethod === PaymentMethod.ALIPAY ? '#e7f4ff' : '#ffffff',
              borderColor: paymentMethod === PaymentMethod.ALIPAY ? '#1890ff' : '#d9d9d9',
              color: paymentMethod === PaymentMethod.ALIPAY ? '#1890ff' : '#000000'
            }}
            onClick={() => handlePaymentMethodChange(PaymentMethod.ALIPAY)}
          >
            支付宝
          </Button>

          <Button
            size="large"
            style={{
              height: '60px',
              display: 'flex',
              flexDirection: 'column',
              justifyContent: 'center',
              alignItems: 'center',
              backgroundColor: paymentMethod === PaymentMethod.WECHAT ? '#e7f4ff' : '#ffffff',
              borderColor: paymentMethod === PaymentMethod.WECHAT ? '#1890ff' : '#d9d9d9',
              color: paymentMethod === PaymentMethod.WECHAT ? '#1890ff' : '#000000'
            }}
            onClick={() => handlePaymentMethodChange(PaymentMethod.WECHAT)}
          >
            微信支付
          </Button>

          <Button
            size="large"
            style={{
              height: '60px',
              display: 'flex',
              flexDirection: 'column',
              justifyContent: 'center',
              alignItems: 'center',
              backgroundColor: paymentMethod === PaymentMethod.UNION_PAY ? '#e7f4ff' : '#ffffff',
              borderColor: paymentMethod === PaymentMethod.UNION_PAY ? '#1890ff' : '#d9d9d9',
              color: paymentMethod === PaymentMethod.UNION_PAY ? '#1890ff' : '#000000'
            }}
            onClick={() => handlePaymentMethodChange(PaymentMethod.UNION_PAY)}
          >
            银联支付
          </Button>

          <Button
            size="large"
            style={{
              height: '60px',
              display: 'flex',
              flexDirection: 'column',
              justifyContent: 'center',
              alignItems: 'center',
              backgroundColor: paymentMethod === PaymentMethod.CREDIT_CARD ? '#e7f4ff' : '#ffffff',
              borderColor: paymentMethod === PaymentMethod.CREDIT_CARD ? '#1890ff' : '#d9d9d9',
              color: paymentMethod === PaymentMethod.CREDIT_CARD ? '#1890ff' : '#000000'
            }}
            onClick={() => handlePaymentMethodChange(PaymentMethod.CREDIT_CARD)}
          >
            信用卡
          </Button>
        </div>

        {/* 全款支付选项 */}
        <div style={{ marginTop: '16px' }}>
          <Button
            size="large"
            style={{
              width: '100%',
              height: '50px',
              backgroundColor: paymentMethod && !selectedInstallment ? '#f0f9ff' : '#f5f5f5',
              borderColor: paymentMethod && !selectedInstallment ? '#1890ff' : '#d9d9d9',
              color: paymentMethod && !selectedInstallment ? '#1890ff' : '#666666'
            }}
            disabled={!paymentMethod}
          >
            {actualAmount.toFixed(2)}元・全款支付
          </Button>
        </div>
      </Card>
    );
  };

  // 渲染分期方式选择组件
  const renderInstallmentOptions = () => {
    if (!showInstallmentOptions) return null;

    // 筛选可用的分期方案
    const availablePlans = installmentPlans.filter(plan => 
      actualAmount >= plan.minAmount && actualAmount <= plan.maxAmount
    );

    console.log('分期方案调试信息:', {
      actualAmount,
      installmentPlansCount: installmentPlans.length,
      installmentPlans,
      availablePlansCount: availablePlans.length,
      availablePlans,
      showInstallmentOptions,
      筛选条件: `${actualAmount} >= minAmount && ${actualAmount} <= maxAmount`
    });

    return (
      <Card title="分期方式" style={{ marginBottom: '16px' }}>
        <Text type="secondary" style={{ marginBottom: '16px', display: 'block' }}>
          仅现金部分参与分期，积分不参与分期
        </Text>
        
        <div style={{ marginBottom: '16px' }}>
          {/* 分期支付选项 */}
          {installmentPlans.length > 0 ? (
            <div style={{ display: 'grid', gridTemplateColumns: '1fr 1fr', gap: '12px', marginBottom: '12px' }}>
              {installmentPlans.map(plan => {
                const monthlyAmount = calculateMonthlyPayment(plan);
                const isSelected = selectedInstallment === plan.id;
                
                return (
                  <Button
                    key={plan.id}
                    size="large"
                    style={{
                      height: '60px',
                      display: 'flex',
                      flexDirection: 'column',
                      justifyContent: 'center',
                      alignItems: 'center',
                      backgroundColor: isSelected ? '#ffe7e7' : '#ffffff',
                      borderColor: isSelected ? '#ff4757' : '#d9d9d9',
                      color: isSelected ? '#ff4757' : '#000000',
                      fontSize: '14px',
                      fontWeight: 'bold'
                    }}
                    onClick={() => setSelectedInstallment(plan.id)}
                  >
                    <div>
                      {monthlyAmount.toFixed(2)}元×{plan.installmentCount}期
                    </div>
                    <div style={{ fontSize: '12px', marginTop: '2px' }}>
                      {plan.interestRate === 0 ? '免息' : '低息'}
                    </div>
                  </Button>
                );
              })}
            </div>
          ) : (
            <div style={{ textAlign: 'center', padding: '20px', color: '#999' }}>
              <div>分期方案加载中...</div>
              <div style={{ fontSize: '12px', marginTop: '8px', color: '#666' }}>
                当前金额: {actualAmount}元，分期方案数量: {installmentPlans.length}
              </div>
            </div>
          )}

          {/* 全款支付按钮 */}
          <Button
            size="large"
            style={{
              width: '100%',
              height: '50px',
              backgroundColor: selectedInstallment ? '#f5f5f5' : '#e7f4ff',
              borderColor: selectedInstallment ? '#d9d9d9' : '#1890ff',
              color: selectedInstallment ? '#666666' : '#1890ff',
              fontSize: '16px'
            }}
            onClick={() => setSelectedInstallment(null)}
          >
            {actualAmount.toFixed(2)}元・全款支付
          </Button>
        </div>

        <Text type="secondary" style={{ fontSize: '12px' }}>
          *免息分期仅支持招商银行信用卡支付，请确保您卡片的可用额度大于订单总金额。
        </Text>
      </Card>
    );
  };

  if (loading) {
    return (
      <div style={{ padding: '50px', textAlign: 'center' }}>
        <Spin size="large" />
      </div>
    );
  }

  return (
    <div style={{ padding: '20px', backgroundColor: '#f5f5f5', minHeight: '100vh' }}>
      <div style={{ maxWidth: '1200px', margin: '0 auto' }}>
        <Title level={2}>确认订单</Title>
        
        <Form form={form} onFinish={handleSubmit} layout="vertical">
          <Row gutter={24}>
            {/* 左侧主要内容 */}
            <Col span={16}>
              {/* 收货地址 */}
              <Card 
                title={
                  <Space>
                    <EnvironmentOutlined />
                    收货地址
                  </Space>
                }
                style={{ marginBottom: '16px' }}
              >
                <Form.Item name="receiverName" label="收货人" rules={[{ required: true, message: '请输入收货人' }]}>
                  <Input placeholder="请输入收货人姓名" />
                </Form.Item>
                <Form.Item name="receiverPhone" label="联系电话" rules={[{ required: true, message: '请输入联系电话' }]}>
                  <Input placeholder="请输入联系电话" />
                </Form.Item>
                <Form.Item name="address" label="详细地址" rules={[{ required: true, message: '请输入详细地址' }]}>
                  <TextArea rows={2} placeholder="请输入详细地址" />
                </Form.Item>
              </Card>

              {/* 商品信息 */}
              <Card title="商品信息" style={{ marginBottom: '16px' }}>
                {selectedItems.map((item: CartItem) => (
                  <div key={item.id} style={{ display: 'flex', padding: '12px 0', borderBottom: '1px solid #f0f0f0' }}>
                    <img 
                      src={item.product.imageUrl || '/placeholder.jpg'} 
                      alt={item.product.name}
                      style={{ width: '80px', height: '80px', objectFit: 'cover', marginRight: '12px' }}
                    />
                    <div style={{ flex: 1 }}>
                      <Text strong>{item.product.name}</Text>
                      <br />
                      <Text type="secondary">数量: {item.quantity}</Text>
                      <br />
                      <Text type="danger">¥{item.product.price.toFixed(2)}</Text>
                    </div>
                    <div>
                      <Text strong>¥{(item.product.price * item.quantity).toFixed(2)}</Text>
                    </div>
                  </div>
                ))}
              </Card>

              {/* 支付方式 */}
              {renderPaymentMethods()}

              {/* 分期方式 */}
              {renderInstallmentOptions()}

              {/* 订单备注 */}
              <Card title="订单备注">
                <Form.Item name="orderRemark">
                  <TextArea 
                    rows={3} 
                    placeholder="请输入订单备注（可选）"
                    maxLength={200}
                  />
                </Form.Item>
              </Card>
            </Col>

            {/* 右侧订单汇总 */}
            <Col span={8}>
              <Card title="订单汇总" style={{ position: 'sticky', top: '20px' }}>
                <div style={{ marginBottom: '12px' }}>
                  <Space style={{ width: '100%', justifyContent: 'space-between' }}>
                    <Text>商品总额:</Text>
                    <Text>¥{totalAmount.toFixed(2)}</Text>
                  </Space>
                </div>
                <div style={{ marginBottom: '12px' }}>
                  <Space style={{ width: '100%', justifyContent: 'space-between' }}>
                    <Text>运费:</Text>
                    <Text>¥{shippingFee.toFixed(2)}</Text>
                  </Space>
                </div>
                
                {/* 分期信息显示 */}
                {selectedInstallment && (
                  <>
                    <Divider />
                    {(() => {
                      const selectedPlan = installmentPlans.find(p => p.id === selectedInstallment);
                      if (selectedPlan) {
                        const monthlyAmount = calculateMonthlyPayment(selectedPlan);
                        return (
                          <div style={{ marginBottom: '12px' }}>
                            <Space style={{ width: '100%', justifyContent: 'space-between' }}>
                              <Text strong>分期方案:</Text>
                              <Text strong style={{ color: '#ff4d4f' }}>
                                {selectedPlan.planName}
                              </Text>
                            </Space>
                            <div style={{ marginTop: '8px' }}>
                              <Space style={{ width: '100%', justifyContent: 'space-between' }}>
                                <Text>每期金额:</Text>
                                <Text strong style={{ color: '#ff4d4f' }}>
                                  ¥{monthlyAmount.toFixed(2)}
                                </Text>
                              </Space>
                            </div>
                          </div>
                        );
                      }
                      return null;
                    })()}
                  </>
                )}
                
                <Divider />
                <div style={{ marginBottom: '20px' }}>
                  <Space style={{ width: '100%', justifyContent: 'space-between' }}>
                    <Text strong>
                      {paymentMethod === PaymentMethod.CREDIT_CARD ? '订单总额:' : '实付款:'}
                    </Text>
                    <Text strong style={{ fontSize: '18px', color: '#ff4d4f' }}>
                      ¥{actualAmount.toFixed(2)}
                    </Text>
                  </Space>
                </div>

                <Button 
                  type="primary" 
                  size="large" 
                  htmlType="submit"
                  loading={submitting}
                  block
                  style={{ height: '50px', fontSize: '16px' }}
                >
                  {paymentMethod === PaymentMethod.CREDIT_CARD ? '确认分期订单' : '提交订单'}
                </Button>
              </Card>
            </Col>
          </Row>
        </Form>
      </div>
    </div>
  );
};

export default CheckoutPage;
