import React, { useEffect } from 'react';
import { 
  Row, 
  Col, 
  Card, 
  Typography, 
  Button, 
  Carousel, 
  Space, 
  Divider,
  List,
  Image,
  Tag,
  Spin,
  Avatar
} from 'antd';
import { 
  ShoppingOutlined, 
  TagsOutlined, 
  FireOutlined,
  RightOutlined,
  NotificationOutlined,
  SoundOutlined,
  ThunderboltOutlined,
  ShoppingCartOutlined
} from '@ant-design/icons';
import { useNavigate } from 'react-router-dom';
import { useDispatch, useSelector } from 'react-redux';
import { AppDispatch, RootState } from '../store';
import { fetchProducts } from '../store/slices/productSlice';
import { fetchAllCategories } from '../store/slices/categorySlice';
import '../styles/HomePage.css';

const { Title, Paragraph, Text } = Typography;
const { Meta } = Card;

// 默认热门商品数据，在API数据加载前显示
const defaultHotProducts = [
  { 
    id: 1, 
    name: "iPhone 14 Pro Max", 
    price: 8999, 
    description: "苹果最新旗舰手机", 
    imageUrl: "https://via.placeholder.com/120x120?text=iPhone" 
  },
  { 
    id: 2, 
    name: "MacBook Air M2", 
    price: 7999, 
    description: "轻薄便携的笔记本电脑", 
    imageUrl: "https://via.placeholder.com/120x120?text=MacBook" 
  },
  { 
    id: 3, 
    name: "AirPods Pro", 
    price: 1999, 
    description: "主动降噪无线耳机", 
    imageUrl: "https://via.placeholder.com/120x120?text=AirPods" 
  },
  { 
    id: 4, 
    name: "Nike Air Jordan", 
    price: 1299, 
    description: "经典篮球鞋", 
    imageUrl: "https://via.placeholder.com/120x120?text=Nike" 
  },
  { 
    id: 5, 
    name: "索尼WH-1000XM5", 
    price: 2899, 
    description: "顶级降噪耳机", 
    imageUrl: "https://via.placeholder.com/120x120?text=Sony" 
  },
  { 
    id: 6, 
    name: "三星Galaxy S23", 
    price: 6999, 
    description: "安卓旗舰手机", 
    imageUrl: "https://via.placeholder.com/120x120?text=Samsung" 
  },
  { 
    id: 7, 
    name: "华为MateBook", 
    price: 5999, 
    description: "高性能笔记本电脑", 
    imageUrl: "https://via.placeholder.com/120x120?text=Huawei" 
  },
  { 
    id: 8, 
    name: "小米手环8", 
    price: 299, 
    description: "智能健康手环", 
    imageUrl: "https://via.placeholder.com/120x120?text=Xiaomi" 
  }
];

const HomePage: React.FC = () => {
  const navigate = useNavigate();
  const dispatch = useDispatch<AppDispatch>();
  
  const { products, loading } = useSelector((state: RootState) => state.products);
  const { allCategories } = useSelector((state: RootState) => state.categories);
  
  useEffect(() => {
    // 获取推荐商品和分类
    dispatch(fetchProducts({ current: 1, size: 12 }));
    dispatch(fetchAllCategories());
  }, [dispatch]);
  
  // 轮播图数据
  const bannerImages = [
    {
      id: 1,
      imageUrl: 'https://via.placeholder.com/1200x400?text=招财商城+促销活动',
      title: '夏季大促',
      link: '/search?keyword=夏季'
    },
    {
      id: 2,
      imageUrl: 'https://via.placeholder.com/1200x400?text=新品上市',
      title: '新品上市',
      link: '/search?keyword=新品'
    },
    {
      id: 3,
      imageUrl: 'https://via.placeholder.com/1200x400?text=限时折扣',
      title: '限时折扣',
      link: '/search?keyword=折扣'
    }
  ];
  
  // 热门商品数据（从products中获取，如果为空则使用默认数据）
  const hotProducts = products && products.length > 0 ? products.slice(0, 8) : defaultHotProducts;
  
  return (
    <div style={{ padding: '24px' }}>
      {/* 热门商品横幅 - 使用图片展示 */}
      <div style={{ marginBottom: 24 }}>
        <Card 
          title={
            <div style={{ display: 'flex', alignItems: 'center' }}>
              <ThunderboltOutlined style={{ color: '#ff4d4f', marginRight: 8 }} />
              <span>热门商品</span>
            </div>
          }
          extra={
            <Button type="link" onClick={() => navigate('/products')}>
              查看更多 <RightOutlined />
            </Button>
          }
          bordered={false}
        >
          {loading ? (
            <div style={{ textAlign: 'center', padding: '20px 0' }}>
              <Spin /> <Text style={{ marginLeft: 8 }}>加载中...</Text>
            </div>
          ) : (
            <div className="hot-products-container" style={{ overflow: 'hidden' }}>
              <div className="hot-products-scroll" style={{ 
                display: 'flex', 
                overflowX: 'auto', 
                padding: '10px 0',
                scrollbarWidth: 'none', // Firefox
                msOverflowStyle: 'none', // IE/Edge
              }}>
                {/* 隐藏滚动条的样式 */}
                <style>
                  {`
                    .hot-products-scroll::-webkit-scrollbar {
                      display: none;
                    }
                  `}
                </style>
                
                {hotProducts.map(product => (
                  <div key={product.id} style={{ 
                    flex: '0 0 auto',
                    width: '180px',
                    marginRight: '16px',
                    cursor: 'pointer'
                  }} onClick={() => navigate(`/product/${product.id}`)}>
                    <Card
                      hoverable
                      cover={
                        <div style={{ 
                          height: '120px', 
                          display: 'flex', 
                          alignItems: 'center', 
                          justifyContent: 'center',
                          overflow: 'hidden',
                          padding: '8px'
                        }}>
                          <img 
                            alt={product.name} 
                            src={product.imageUrl || 'https://via.placeholder.com/120x120?text=商品图片'} 
                            style={{ maxWidth: '100%', maxHeight: '100%', objectFit: 'contain' }}
                          />
                        </div>
                      }
                      bodyStyle={{ padding: '12px' }}
                    >
                      <div style={{ marginBottom: '8px' }}>
                        <Text ellipsis style={{ fontSize: '14px', fontWeight: 'bold' }}>
                          {product.name}
                        </Text>
                      </div>
                      <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
                        <Text type="danger" strong>¥{product.price?.toFixed(2)}</Text>
                        <Button 
                          type="primary" 
                          shape="circle" 
                          icon={<ShoppingCartOutlined />} 
                          size="small"
                          onClick={(e) => {
                            e.stopPropagation();
                            // 这里可以添加加入购物车的逻辑
                          }}
                        />
                      </div>
                    </Card>
                  </div>
                ))}
              </div>
            </div>
          )}
        </Card>
      </div>
      
      {/* 轮播图 */}
      <Card bordered={false} style={{ marginBottom: 24 }}>
        <Carousel autoplay effect="fade">
          {bannerImages.map(banner => (
            <div key={banner.id} onClick={() => navigate(banner.link)} style={{ cursor: 'pointer' }}>
              <img 
                src={banner.imageUrl} 
                alt={banner.title} 
                style={{ width: '100%', height: 'auto', maxHeight: '400px', objectFit: 'cover' }}
              />
            </div>
          ))}
        </Carousel>
      </Card>
      
      {/* 欢迎信息 */}
      <Card bordered={false} style={{ marginBottom: 24, textAlign: 'center' }}>
        <Title level={2} style={{ color: '#1890ff' }}>欢迎来到招财商城</Title>
        <Paragraph style={{ fontSize: 16 }}>
          我们提供优质的商品和卓越的服务，让您的购物体验更加愉悦！
        </Paragraph>
        <Button 
          type="primary" 
          size="large" 
          icon={<ShoppingOutlined />}
          onClick={() => navigate('/search')}
          style={{ margin: '16px 0' }}
        >
          开始购物
        </Button>
      </Card>
      
      {/* 商品分类 */}
      <div className="home-section">
        <div className="section-header">
          <Title level={4} className="section-title">
            <TagsOutlined /> 商品分类
          </Title>
          <Button type="link" onClick={() => navigate('/search')}>
            查看全部 <RightOutlined />
          </Button>
        </div>
        
        <Row gutter={[16, 16]}>
          {allCategories.slice(0, 8).map(category => (
            <Col xs={12} sm={8} md={6} lg={4} xl={3} key={category.id}>
              <Card
                hoverable
                style={{ textAlign: 'center' }}
                onClick={() => navigate(`/search?categoryId=${category.id}`)}
              >
                <div style={{ padding: '12px 0' }}>
                  <div style={{ 
                    fontSize: 36, 
                    color: '#1890ff',
                    marginBottom: 8
                  }}>
                    <TagsOutlined />
                  </div>
                  <div>{category.name}</div>
                </div>
              </Card>
            </Col>
          ))}
        </Row>
      </div>
      
      {/* 推荐商品 */}
      <div className="home-section">
        <div className="section-header">
          <Title level={4} className="section-title">
            <FireOutlined /> 热门推荐
          </Title>
          <Button type="link" onClick={() => navigate('/search')}>
            查看更多 <RightOutlined />
          </Button>
        </div>
        
        <Row gutter={[16, 16]}>
          {products.slice(0, 8).map(product => (
            <Col xs={24} sm={12} md={8} lg={6} key={product.id}>
              <Card
                hoverable
                className="product-card"
                cover={
                  <div style={{ height: 200, overflow: 'hidden', display: 'flex', alignItems: 'center', justifyContent: 'center' }}>
                    <img 
                      alt={product.name} 
                      src={product.imageUrl || 'https://via.placeholder.com/300x300?text=商品图片'} 
                      style={{ width: '100%', height: 'auto' }}
                    />
                  </div>
                }
                onClick={() => navigate(`/product/${product.id}`)}
              >
                <Meta
                  title={product.name}
                  description={
                    <div>
                      <div className="product-price">
                        ¥{product.price.toFixed(2)}
                      </div>
                      <div className="product-description">
                        {product.description?.substring(0, 40) || '暂无描述'}
                        {product.description?.length > 40 ? '...' : ''}
                      </div>
                    </div>
                  }
                />
              </Card>
            </Col>
          ))}
        </Row>
      </div>
      
      {/* 商城特点 */}
      <Card bordered={false} style={{ marginBottom: 24 }}>
        <Row gutter={24}>
          <Col span={8}>
            <div style={{ textAlign: 'center', padding: '24px 0' }}>
              <div style={{ fontSize: 48, color: '#1890ff', marginBottom: 16 }}>
                <ShoppingOutlined />
              </div>
              <Title level={4}>正品保障</Title>
              <Paragraph>所有商品均为正品，假一赔十</Paragraph>
            </div>
          </Col>
          <Col span={8}>
            <div style={{ textAlign: 'center', padding: '24px 0' }}>
              <div style={{ fontSize: 48, color: '#1890ff', marginBottom: 16 }}>
                <ShoppingOutlined />
              </div>
              <Title level={4}>极速配送</Title>
              <Paragraph>当日下单，次日送达</Paragraph>
            </div>
          </Col>
          <Col span={8}>
            <div style={{ textAlign: 'center', padding: '24px 0' }}>
              <div style={{ fontSize: 48, color: '#1890ff', marginBottom: 16 }}>
                <ShoppingOutlined />
              </div>
              <Title level={4}>无忧退换</Title>
              <Paragraph>7天无理由退换，售后无忧</Paragraph>
            </div>
          </Col>
        </Row>
      </Card>
    </div>
  );
};

export default HomePage;
