import React, { useState, useEffect, useRef } from 'react';
import './CustomerServiceChat.css';

interface ChatMessage {
  id: string;
  type: 'user' | 'bot';
  message: string;
  timestamp: Date;
  suggestions?: string[];
  promotionInfo?: {
    type: string;
    pointsEarned?: number;
    discountAmount?: number;
    installmentOptions?: number[];
  };
  cacheHit?: boolean;
  responseTimeMs?: number;
}

interface ChatContext {
  productId?: number;
  productPrice?: number;
  currentPage?: string;
}

interface CustomerServiceChatProps {
  isVisible: boolean;
  onClose: () => void;
  context?: ChatContext;
  userId?: number;
}

const CustomerServiceChat: React.FC<CustomerServiceChatProps> = ({
  isVisible,
  onClose,
  context,
  userId = 1
}) => {
  const [messages, setMessages] = useState<ChatMessage[]>([]);
  const [inputMessage, setInputMessage] = useState('');
  const [isLoading, setIsLoading] = useState(false);
  const [sessionId] = useState(() => 'session_' + Date.now());
  const messagesEndRef = useRef<HTMLDivElement>(null);

  const scrollToBottom = () => {
    messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  };

  useEffect(() => {
    scrollToBottom();
  }, [messages]);

  useEffect(() => {
    if (isVisible && messages.length === 0) {
      // ÂèëÈÄÅÂàùÂßãÊ¨¢ËøéÊ∂àÊÅØ
      const welcomeMessage: ChatMessage = {
        id: 'welcome',
        type: 'bot',
        message: 'ÊÇ®Â•ΩÔºÅÊàëÊòØÊÇ®ÁöÑ‰∏ìÂ±ûË¥≠Áâ©Âä©ÊâãÔºåÊúâ‰ªÄ‰πàÂèØ‰ª•Â∏ÆÊÇ®ÁöÑÂêóÔºü\n\nüí°Â∞èË¥¥Â£´Ôºö‰ΩøÁî®ÊãõÂïÜÈì∂Ë°å‰ø°Áî®Âç°ÊîØ‰ªòÂèØ‰∫´ÂèóÊõ¥Â§öÊùÉÁõäÂì¶ÔΩû',
        timestamp: new Date(),
        suggestions: ['‰∫ÜËß£‰ø°Áî®Âç°ÊùÉÁõä', 'Êü•ÁúãÂàÜÊúüÊñπÊ°à', 'Âí®ËØ¢ÂïÜÂìÅÈóÆÈ¢ò']
      };
      setMessages([welcomeMessage]);
    }
  }, [isVisible]);

  const sendMessage = async (message: string) => {
    if (!message.trim()) return;

    // Ê∑ªÂä†Áî®Êà∑Ê∂àÊÅØ
    const userMessage: ChatMessage = {
      id: 'user_' + Date.now(),
      type: 'user',
      message: message.trim(),
      timestamp: new Date()
    };
    
    setMessages(prev => [...prev, userMessage]);
    setInputMessage('');
    setIsLoading(true);

    try {
      const response = await fetch('/api/customer-service/chat', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          userId,
          sessionId,
          message: message.trim(),
          context
        }),
      });

      const result = await response.json();
      
      if (result.code === 200) {
        const botMessage: ChatMessage = {
          id: result.data.responseId,
          type: 'bot',
          message: result.data.message,
          timestamp: new Date(),
          suggestions: result.data.suggestions,
          promotionInfo: result.data.promotionInfo,
          cacheHit: result.data.cacheHit,
          responseTimeMs: result.data.responseTimeMs
        };

        // ‰ΩøÁî®ÊâìÂ≠óÊú∫ÊïàÊûúÊòæÁ§∫Êú∫Âô®‰∫∫Ê∂àÊÅØ
        setMessages(prev => [...prev, { ...botMessage, message: '' }]);
        
        // ÊâìÂ≠óÊú∫ÊïàÊûú
        let currentText = '';
        const fullText = result.data.message;
        const typingSpeed = 30; // ÊØ´Áßí
        
        for (let i = 0; i < fullText.length; i++) {
          setTimeout(() => {
            currentText += fullText[i];
            setMessages(prev => prev.map(msg => 
              msg.id === botMessage.id 
                ? { ...msg, message: currentText }
                : msg
            ));
          }, i * typingSpeed);
        }
      } else {
        const errorMessage: ChatMessage = {
          id: 'error_' + Date.now(),
          type: 'bot',
          message: 'Êä±Ê≠âÔºåÊúçÂä°ÊöÇÊó∂‰∏çÂèØÁî®ÔºåËØ∑Á®çÂêéÂÜçËØï„ÄÇ',
          timestamp: new Date()
        };
        setMessages(prev => [...prev, errorMessage]);
      }
    } catch (error) {
      const errorMessage: ChatMessage = {
        id: 'error_' + Date.now(),
        type: 'bot',
        message: 'ÁΩëÁªúËøûÊé•Â§±Ë¥•ÔºåËØ∑Ê£ÄÊü•ÁΩëÁªúÂêéÈáçËØï„ÄÇ',
        timestamp: new Date()
      };
      setMessages(prev => [...prev, errorMessage]);
    } finally {
      setIsLoading(false);
    }
  };

  const handleSuggestionClick = (suggestion: string) => {
    sendMessage(suggestion);
  };

  const handleKeyPress = (e: React.KeyboardEvent) => {
    if (e.key === 'Enter' && !e.shiftKey) {
      e.preventDefault();
      sendMessage(inputMessage);
    }
  };

  if (!isVisible) return null;

  return (
    <div className="customer-service-chat">
      <div className="chat-header">
        <div className="header-info">
          <h3>Êô∫ËÉΩÂÆ¢Êúç</h3>
          <span className="status">Âú®Á∫ø</span>
        </div>
        <button className="close-btn" onClick={onClose}>√ó</button>
      </div>

      <div className="chat-messages">
        {messages.map((message) => (
          <div key={message.id} className={`message ${message.type}`}>
            <div className="message-content">
              <div className="message-text">
                {message.message.split('\n').map((line, index) => (
                  <div key={index}>{line}</div>
                ))}
              </div>
              
              {message.suggestions && message.suggestions.length > 0 && (
                <div className="suggestions">
                  {message.suggestions.map((suggestion, index) => (
                    <button
                      key={index}
                      className="suggestion-btn"
                      onClick={() => handleSuggestionClick(suggestion)}
                    >
                      {suggestion}
                    </button>
                  ))}
                </div>
              )}

              {message.promotionInfo && (
                <div className="promotion-info">
                  <div className="promotion-header">üéÅ ‰∏ìÂ±û‰ºòÊÉ†</div>
                  {message.promotionInfo.pointsEarned && (
                    <div>ÁßØÂàÜÂ•ñÂä±: {message.promotionInfo.pointsEarned}ÂàÜ</div>
                  )}
                  {message.promotionInfo.discountAmount && (
                    <div>‰ºòÊÉ†ÈáëÈ¢ù: ¬•{message.promotionInfo.discountAmount.toFixed(2)}</div>
                  )}
                  {message.promotionInfo.installmentOptions && (
                    <div>
                      ÂàÜÊúüÈÄâÊã©: {message.promotionInfo.installmentOptions.join('/')}ÊúüÂÖçÊÅØ
                    </div>
                  )}
                </div>
              )}

              {message.cacheHit !== undefined && process.env.NODE_ENV === 'development' && (
                <div className="debug-info">
                  <small>
                    {message.cacheHit ? 'ÁºìÂ≠òÂëΩ‰∏≠' : 'ÂÆûÊó∂ÁîüÊàê'} 
                    {message.responseTimeMs && ` | ${message.responseTimeMs}ms`}
                  </small>
                </div>
              )}
            </div>
            <div className="message-time">
              {message.timestamp.toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' })}
            </div>
          </div>
        ))}
        
        {isLoading && (
          <div className="message bot">
            <div className="message-content">
              <div className="typing-indicator">
                <span></span>
                <span></span>
                <span></span>
              </div>
            </div>
          </div>
        )}
        
        <div ref={messagesEndRef} />
      </div>

      <div className="chat-input">
        <textarea
          value={inputMessage}
          onChange={(e) => setInputMessage(e.target.value)}
          onKeyPress={handleKeyPress}
          placeholder="ËæìÂÖ•ÊÇ®ÁöÑÈóÆÈ¢ò..."
          rows={2}
          disabled={isLoading}
        />
        <button
          onClick={() => sendMessage(inputMessage)}
          disabled={isLoading || !inputMessage.trim()}
          className="send-btn"
        >
          ÂèëÈÄÅ
        </button>
      </div>
    </div>
  );
};

export default CustomerServiceChat;
