import React, { useEffect, useState } from 'react';
import { 
  Card, 
  Row, 
  Col, 
  Pagination, 
  Spin, 
  Alert, 
  Select, 
  Button, 
  Space, 
  Typography,
  Image,
  Tag,
  InputNumber,
  Rate,
  Drawer,
  Tabs,
  Cascader,
  Badge,
  Tooltip
} from 'antd';
import { 
  ShoppingCartOutlined, 
  EyeOutlined, 
  HeartOutlined, 
  FireOutlined,
  StarOutlined,
  FilterOutlined
} from '@ant-design/icons';
import { useDispatch, useSelector } from 'react-redux';
import { useNavigate } from 'react-router-dom';
import { RootState, AppDispatch } from '../store';
import { fetchProducts, setFilters, clearFilters } from '../store/slices/productSlice';
import { fetchAllCategories } from '../store/slices/categorySlice';
import { Product } from '../types';

const { Title, Text, Paragraph } = Typography;
const { Option } = Select;
const { TabPane } = Tabs;

const ProductList: React.FC = () => {
  const dispatch = useDispatch<AppDispatch>();
  const navigate = useNavigate();
  const { 
    products, 
    total, 
    current, 
    size, 
    loading, 
    error, 
    filters 
  } = useSelector((state: RootState) => state.products);
  
  const { allCategories } = useSelector((state: RootState) => state.categories);
  
  // 新增状态
  const [previewVisible, setPreviewVisible] = useState(false);
  const [previewProduct, setPreviewProduct] = useState<Product | null>(null);
  const [categoryOptions, setCategoryOptions] = useState<any[]>([]);
  
  useEffect(() => {
    // 初始化时获取商品列表和分类
    dispatch(fetchProducts({ current: 1, size: 12 }));
    dispatch(fetchAllCategories());
  }, [dispatch]);
  
  // 处理分类数据，转换为级联选择器格式
  useEffect(() => {
    if (allCategories.length > 0) {
      const topCategories = allCategories.filter(c => !c.parentId);
      const options = topCategories.map(top => ({
        value: top.id,
        label: top.name,
        children: allCategories
          .filter(c => c.parentId === top.id)
          .map(sub => ({
            value: sub.id,
            label: sub.name
          }))
      }));
      setCategoryOptions(options);
    }
  }, [allCategories]);
  
  // 处理分页变化
  const handlePageChange = (page: number, pageSize?: number) => {
    dispatch(fetchProducts({ 
      current: page, 
      size: pageSize || size,
      ...filters 
    }));
  };
  
  // 处理分类筛选
  const handleCategoryChange = (categoryId: number | undefined) => {
    const newFilters = { ...filters, categoryId };
    dispatch(setFilters(newFilters));
    dispatch(fetchProducts({ 
      current: 1, 
      size,
      ...newFilters 
    }));
  };
  
  // 处理价格筛选
  const handleMinPriceChange = (value: number | null) => {
    const newFilters = { ...filters, minPrice: value || undefined };
    dispatch(setFilters(newFilters));
    dispatch(fetchProducts({ 
      current: 1, 
      size,
      ...newFilters 
    }));
  };
  
  const handleMaxPriceChange = (value: number | null) => {
    const newFilters = { ...filters, maxPrice: value || undefined };
    dispatch(setFilters(newFilters));
    dispatch(fetchProducts({ 
      current: 1, 
      size,
      ...newFilters 
    }));
  };
  
  // 处理排序变化
  const handleSortChange = (sortBy: string) => {
    const newFilters = { ...filters, sortBy };
    dispatch(setFilters(newFilters));
    dispatch(fetchProducts({ 
      current: 1, 
      size,
      ...newFilters 
    }));
  };
  
  // 清除所有筛选条件
  const handleClearFilters = () => {
    dispatch(clearFilters());
    dispatch(fetchProducts({ current: 1, size }));
  };
  
  // 跳转到商品详情页
  const handleProductClick = (productId: number) => {
    navigate(`/product/${productId}`);
  };
  
  // 商品快速预览
  const handlePreview = (e: React.MouseEvent, product: Product) => {
    e.stopPropagation();
    setPreviewProduct(product);
    setPreviewVisible(true);
  };
  
  // 关闭预览
  const handleClosePreview = () => {
    setPreviewVisible(false);
    setPreviewProduct(null);
  };
  
  // 渲染商品卡片
  const renderProductCard = (product: Product) => (
    <Col xs={24} sm={12} md={8} lg={6} key={product.id}>
      <Badge.Ribbon 
        text={product.salesCount > 100 ? "热销" : ""} 
        color="red" 
        style={{ display: product.salesCount > 100 ? 'block' : 'none' }}
      >
        <Card
          hoverable
          style={{ marginBottom: 16 }}
          onClick={() => handleProductClick(product.id)}
          cover={
            <div style={{ position: 'relative' }}>
              <Image
                alt={product.name}
                src={product.imageUrl || 'https://via.placeholder.com/300x300?text=暂无图片'}
                height={200}
                style={{ objectFit: 'cover' }}
                preview={false}
              />
              <div 
                style={{ 
                  position: 'absolute', 
                  top: 8, 
                  right: 8, 
                  background: 'rgba(255,255,255,0.8)', 
                  borderRadius: '50%', 
                  width: 32, 
                  height: 32, 
                  display: 'flex', 
                  alignItems: 'center', 
                  justifyContent: 'center',
                  cursor: 'pointer'
                }}
                onClick={(e) => handlePreview(e, product)}
              >
                <EyeOutlined style={{ fontSize: 18 }} />
              </div>
            </div>
          }
          actions={[
            <Tooltip title="查看详情">
              <Button 
                type="text" 
                icon={<EyeOutlined />} 
                onClick={(e) => {
                  e.stopPropagation();
                  handleProductClick(product.id);
                }}
              />
            </Tooltip>,
            <Tooltip title="加入购物车">
              <Button 
                type="text" 
                icon={<ShoppingCartOutlined />} 
                onClick={(e) => e.stopPropagation()}
              />
            </Tooltip>,
            <Tooltip title="收藏">
              <Button 
                type="text" 
                icon={<HeartOutlined />} 
                onClick={(e) => e.stopPropagation()}
              />
            </Tooltip>
          ]}
        >
          <Card.Meta
            title={
              <div style={{ height: 44, overflow: 'hidden' }}>
                <Text ellipsis={{ tooltip: product.name }}>
                  {product.name}
                </Text>
              </div>
            }
            description={
              <div>
                <div style={{ marginBottom: 8 }}>
                  <Text strong style={{ color: '#ff4d4f', fontSize: 18 }}>
                    ¥{product.price}
                  </Text>
                </div>
                <div style={{ marginBottom: 4, display: 'flex', alignItems: 'center' }}>
                  <Rate allowHalf defaultValue={product.rating || 5} disabled style={{ fontSize: 12 }} />
                  <Text type="secondary" style={{ marginLeft: 8 }}>
                    ({product.reviewCount || 0})
                  </Text>
                </div>
                <div style={{ marginBottom: 4 }}>
                  <Text type="secondary">销量: {product.salesCount}</Text>
                </div>
                {product.categoryName && (
                  <Tag color="blue">{product.categoryName}</Tag>
                )}
              </div>
            }
          />
        </Card>
      </Badge.Ribbon>
    </Col>
  );
  
  // 商品快速预览抽屉
  const renderPreviewDrawer = () => (
    <Drawer
      title="商品预览"
      placement="right"
      width={500}
      onClose={handleClosePreview}
      open={previewVisible}
      extra={
        <Space>
          <Button onClick={handleClosePreview}>关闭</Button>
          <Button 
            type="primary" 
            onClick={() => {
              handleClosePreview();
              if (previewProduct) {
                handleProductClick(previewProduct.id);
              }
            }}
          >
            查看详情
          </Button>
        </Space>
      }
    >
      {previewProduct && (
        <div>
          <Image
            src={previewProduct.imageUrl || 'https://via.placeholder.com/400x400?text=暂无图片'}
            alt={previewProduct.name}
            width="100%"
          />
          <Title level={4} style={{ marginTop: 16 }}>{previewProduct.name}</Title>
          <div style={{ marginBottom: 16 }}>
            <Text strong style={{ color: '#ff4d4f', fontSize: 24 }}>
              ¥{previewProduct.price}
            </Text>
          </div>
          <div style={{ marginBottom: 16 }}>
            <Rate allowHalf defaultValue={previewProduct.rating || 5} disabled />
            <Text type="secondary" style={{ marginLeft: 8 }}>
              ({previewProduct.reviewCount || 0}评价)
            </Text>
          </div>
          <Paragraph ellipsis={{ rows: 3 }}>
            {previewProduct.description || '暂无商品描述'}
          </Paragraph>
          <div style={{ marginTop: 16 }}>
            <Space>
              <Button 
                type="primary" 
                icon={<ShoppingCartOutlined />}
              >
                加入购物车
              </Button>
              <Button icon={<HeartOutlined />}>
                收藏
              </Button>
            </Space>
          </div>
        </div>
      )}
    </Drawer>
  );
  
  if (error) {
    return (
      <Alert
        message="加载失败"
        description={error}
        type="error"
        showIcon
        action={
          <Button size="small" danger onClick={() => dispatch(fetchProducts({ current: 1, size }))}>
            重试
          </Button>
        }
      />
    );
  }
  
  return (
    <div style={{ padding: '20px' }}>
      {/* 页面标题 */}
      <Title level={2}>商品列表</Title>
      
      {/* 筛选条件 */}
      <Card style={{ marginBottom: 20 }}>
        <Tabs defaultActiveKey="filter">
          <TabPane tab={<span><FilterOutlined />筛选</span>} key="filter">
            <Space direction="vertical" size="middle" style={{ width: '100%' }}>
              <Space wrap>
                <div>
                  <Text strong>分类筛选：</Text>
                  <Cascader
                    options={categoryOptions}
                    placeholder="选择分类"
                    style={{ width: 300, marginLeft: 8 }}
                    onChange={(value) => handleCategoryChange(value ? value[value.length - 1] as number : undefined)}
                    changeOnSelect
                  />
                </div>
                
                <div>
                  <Text strong>排序方式：</Text>
                  <Select
                    style={{ width: 150, marginLeft: 8 }}
                    placeholder="选择排序"
                    value={filters.sortBy}
                    onChange={handleSortChange}
                  >
                    <Option value="price_asc">价格从低到高</Option>
                    <Option value="price_desc">价格从高到低</Option>
                    <Option value="sales_desc">销量从高到低</Option>
                    <Option value="rating_desc">评分从高到低</Option>
                    <Option value="created_desc">最新发布</Option>
                  </Select>
                </div>
                
                <Button onClick={handleClearFilters}>
                  清除筛选
                </Button>
              </Space>
              
              <Space wrap>
                <Text strong>价格范围：</Text>
                <InputNumber
                  placeholder="最低价格"
                  style={{ width: 120 }}
                  min={0}
                  value={filters.minPrice}
                  onChange={handleMinPriceChange}
                />
                <Text>至</Text>
                <InputNumber
                  placeholder="最高价格"
                  style={{ width: 120 }}
                  min={0}
                  value={filters.maxPrice}
                  onChange={handleMaxPriceChange}
                />
              </Space>
            </Space>
          </TabPane>
          <TabPane tab={<span><FireOutlined />热销</span>} key="hot">
            <Button 
              type="link" 
              onClick={() => {
                dispatch(setFilters({ sortBy: 'sales_desc' }));
                dispatch(fetchProducts({ current: 1, size, sortBy: 'sales_desc' }));
              }}
            >
              查看热销商品
            </Button>
          </TabPane>
          <TabPane tab={<span><StarOutlined />好评</span>} key="rated">
            <Button 
              type="link" 
              onClick={() => {
                dispatch(setFilters({ sortBy: 'rating_desc' }));
                dispatch(fetchProducts({ current: 1, size, sortBy: 'rating_desc' }));
              }}
            >
              查看好评商品
            </Button>
          </TabPane>
        </Tabs>
      </Card>
      
      {/* 商品列表 */}
      <Spin spinning={loading}>
        <Row gutter={[16, 16]}>
          {products.map(renderProductCard)}
        </Row>
        
        {products.length === 0 && !loading && (
          <div style={{ textAlign: 'center', padding: '40px 0' }}>
            <Text type="secondary">暂无商品数据</Text>
          </div>
        )}
      </Spin>
      
      {/* 分页 */}
      {total > 0 && (
        <div style={{ textAlign: 'center', marginTop: 32 }}>
          <Pagination
            current={current}
            total={total}
            pageSize={size}
            showSizeChanger
            showQuickJumper
            showTotal={(total, range) => 
              `第 ${range[0]}-${range[1]} 条，共 ${total} 条`
            }
            onChange={handlePageChange}
            onShowSizeChange={handlePageChange}
          />
        </div>
      )}
      
      {/* 商品预览抽屉 */}
      {renderPreviewDrawer()}
    </div>
  );
};

export default ProductList;
