import React, { useState, useEffect } from 'react';
import { 
  Card, 
  Typography, 
  Radio, 
  Space, 
  Button, 
  Steps,
  message,
  Result
} from 'antd';
import { 
  CreditCardOutlined, 
  BankOutlined, 
  WalletOutlined,
  CheckCircleOutlined
} from '@ant-design/icons';
import { useLocation, useNavigate } from 'react-router-dom';

const { Title, Text } = Typography;
const { Step } = Steps;

interface PaymentPageState {
  orderId: string;
  amount: number;
}

const PaymentPage: React.FC = () => {
  const location = useLocation();
  const navigate = useNavigate();
  
  // 从路由状态中获取数据
  const state = location.state as PaymentPageState;
  
  // 如果没有传递状态，重定向到首页
  useEffect(() => {
    if (!state || !state.orderId) {
      navigate('/');
      message.error('支付信息不完整');
    }
  }, [state, navigate]);
  
  // 支付状态
  const [paymentMethod, setPaymentMethod] = useState<string>('credit_card');
  const [paymentStep, setPaymentStep] = useState<number>(0);
  const [processing, setProcessing] = useState<boolean>(false);
  
  // 处理支付方式选择
  const handlePaymentMethodChange = (e: any) => {
    setPaymentMethod(e.target.value);
  };
  
  // 处理支付提交
  const handlePaymentSubmit = () => {
    setProcessing(true);
    
    // 模拟支付处理
    setTimeout(() => {
      setProcessing(false);
      setPaymentStep(1);
    }, 2000);
  };
  
  // 渲染支付方式
  const renderPaymentMethods = () => {
    return (
      <div>
        <Title level={4}>支付方式</Title>
        <Radio.Group value={paymentMethod} onChange={handlePaymentMethodChange}>
          <Space direction="vertical" style={{ width: '100%' }}>
            <Radio value="credit_card">
              <Space>
                <CreditCardOutlined />
                信用卡支付
              </Space>
            </Radio>
            <Radio value="bank_transfer">
              <Space>
                <BankOutlined />
                银行转账
              </Space>
            </Radio>
            <Radio value="balance">
              <Space>
                <WalletOutlined />
                余额支付
              </Space>
            </Radio>
          </Space>
        </Radio.Group>
      </div>
    );
  };
  
  if (!state) {
    return null;
  }
  
  return (
    <div style={{ padding: '20px', maxWidth: 800, margin: '0 auto' }}>
      <Card>
        <Steps current={paymentStep} style={{ marginBottom: 40 }}>
          <Step title="选择支付方式" />
          <Step title="支付完成" />
        </Steps>
        
        {paymentStep === 0 && (
          <div>
            <div style={{ marginBottom: 24 }}>
              <Title level={4}>订单信息</Title>
              <div style={{ background: '#f5f5f5', padding: 16, borderRadius: 4 }}>
                <div style={{ display: 'flex', justifyContent: 'space-between', marginBottom: 8 }}>
                  <Text>订单编号:</Text>
                  <Text>{state.orderId}</Text>
                </div>
                <div style={{ display: 'flex', justifyContent: 'space-between' }}>
                  <Text>应付金额:</Text>
                  <Text strong style={{ color: '#ff4d4f', fontSize: 16 }}>
                    ¥{state.amount.toFixed(2)}
                  </Text>
                </div>
              </div>
            </div>
            
            {renderPaymentMethods()}
            
            <div style={{ marginTop: 24, textAlign: 'center' }}>
              <Button
                type="primary"
                size="large"
                onClick={handlePaymentSubmit}
                loading={processing}
              >
                确认支付
              </Button>
            </div>
          </div>
        )}
        
        {paymentStep === 1 && (
          <Result
            status="success"
            icon={<CheckCircleOutlined style={{ color: '#52c41a' }} />}
            title="支付成功"
            subTitle={`订单号: ${state.orderId}`}
            extra={[
              <Button 
                type="primary" 
                key="orders" 
                onClick={() => navigate('/orders')}
              >
                查看订单
              </Button>,
              <Button 
                key="home" 
                onClick={() => navigate('/')}
              >
                返回首页
              </Button>
            ]}
          />
        )}
      </Card>
    </div>
  );
};

export default PaymentPage;
