import React, { useState, useEffect } from 'react';
import { Input, Button, Space, Dropdown, Menu, Tag, Typography, Divider } from 'antd';
import { SearchOutlined, HistoryOutlined, FireOutlined, DeleteOutlined } from '@ant-design/icons';
import { useDispatch } from 'react-redux';
import { useNavigate } from 'react-router-dom';
import { AppDispatch } from '../store';
import { searchProducts } from '../store/slices/productSlice';

const { Search } = Input;
const { Text } = Typography;

// 最大搜索历史记录数
const MAX_HISTORY = 10;

// 热门搜索关键词（实际项目中应该从后端获取）
const HOT_KEYWORDS = ['手机', '电脑', '耳机', '衣服', '鞋子', '零食', '家具'];

interface SearchBarProps {
  placeholder?: string;
  size?: 'small' | 'middle' | 'large';
  style?: React.CSSProperties;
  onSearch?: (keyword: string) => void;
}

const SearchBar: React.FC<SearchBarProps> = ({
  placeholder = "搜索商品...",
  size = 'middle',
  style,
  onSearch
}) => {
  const [keyword, setKeyword] = useState('');
  const [visible, setVisible] = useState(false);
  const [searchHistory, setSearchHistory] = useState<string[]>([]);
  
  const dispatch = useDispatch<AppDispatch>();
  const navigate = useNavigate();
  
  // 从localStorage加载搜索历史
  useEffect(() => {
    const history = localStorage.getItem('searchHistory');
    if (history) {
      setSearchHistory(JSON.parse(history));
    }
  }, []);
  
  // 保存搜索历史到localStorage
  const saveSearchHistory = (history: string[]) => {
    localStorage.setItem('searchHistory', JSON.stringify(history));
    setSearchHistory(history);
  };
  
  // 添加搜索历史
  const addToHistory = (value: string) => {
    if (!value.trim()) return;
    
    // 移除重复项并将新项放在最前面
    const newHistory = [
      value,
      ...searchHistory.filter(item => item !== value)
    ].slice(0, MAX_HISTORY);
    
    saveSearchHistory(newHistory);
  };
  
  // 清除单个搜索历史
  const removeFromHistory = (value: string, e: React.MouseEvent) => {
    e.stopPropagation();
    const newHistory = searchHistory.filter(item => item !== value);
    saveSearchHistory(newHistory);
  };
  
  // 清除所有搜索历史
  const clearAllHistory = (e: React.MouseEvent) => {
    e.stopPropagation();
    saveSearchHistory([]);
  };
  
  // 执行搜索
  const handleSearch = (value: string) => {
    const searchKeyword = value.trim();
    if (searchKeyword) {
      // 添加到搜索历史
      addToHistory(searchKeyword);
      
      // 执行搜索
      dispatch(searchProducts({ 
        keyword: searchKeyword,
        current: 1,
        size: 12 
      }));
      
      // 跳转到搜索结果页面
      navigate(`/search?keyword=${encodeURIComponent(searchKeyword)}`);
      
      // 关闭下拉菜单
      setVisible(false);
      
      // 如果有自定义搜索回调，也执行它
      if (onSearch) {
        onSearch(searchKeyword);
      }
    }
  };
  
  // 搜索下拉菜单内容
  const dropdownContent = (
    <div style={{ width: 300, padding: '8px 0' }}>
      {/* 搜索历史 */}
      <div style={{ padding: '0 12px' }}>
        <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', marginBottom: 8 }}>
          <Text strong><HistoryOutlined /> 搜索历史</Text>
          {searchHistory.length > 0 && (
            <Button 
              type="text" 
              size="small" 
              icon={<DeleteOutlined />} 
              onClick={clearAllHistory}
            >
              清除
            </Button>
          )}
        </div>
        
        {searchHistory.length > 0 ? (
          <div style={{ display: 'flex', flexWrap: 'wrap', gap: 8, marginBottom: 12 }}>
            {searchHistory.map((item, index) => (
              <Tag 
                key={index} 
                color="blue" 
                style={{ cursor: 'pointer' }}
                onClick={() => handleSearch(item)}
                closable
                onClose={(e) => removeFromHistory(item, e)}
              >
                {item}
              </Tag>
            ))}
          </div>
        ) : (
          <Text type="secondary" style={{ display: 'block', marginBottom: 12 }}>
            暂无搜索历史
          </Text>
        )}
      </div>
      
      <Divider style={{ margin: '8px 0' }} />
      
      {/* 热门搜索 */}
      <div style={{ padding: '0 12px' }}>
        <div style={{ marginBottom: 8 }}>
          <Text strong><FireOutlined /> 热门搜索</Text>
        </div>
        
        <div style={{ display: 'flex', flexWrap: 'wrap', gap: 8 }}>
          {HOT_KEYWORDS.map((item, index) => (
            <Tag 
              key={index} 
              color="red" 
              style={{ cursor: 'pointer' }}
              onClick={() => handleSearch(item)}
            >
              {item}
            </Tag>
          ))}
        </div>
      </div>
    </div>
  );
  
  return (
    <Dropdown
      overlay={dropdownContent}
      trigger={['click']}
      open={visible}
      onOpenChange={setVisible}
      getPopupContainer={(trigger) => trigger.parentElement as HTMLElement}
    >
      <Search
        placeholder={placeholder}
        size={size}
        style={style}
        value={keyword}
        onChange={(e) => setKeyword(e.target.value)}
        onSearch={handleSearch}
        onClick={() => setVisible(true)}
        enterButton={
          <Button type="primary" icon={<SearchOutlined />}>
            搜索
          </Button>
        }
        allowClear
      />
    </Dropdown>
  );
};

export default SearchBar;
