import React, { useState, useEffect } from 'react';
import { 
  Card, 
  Typography, 
  Radio, 
  Space, 
  Button, 
  Steps,
  message,
  Result,
  Spin,
  Divider,
  Modal,
  Image
} from 'antd';
import { 
  CreditCardOutlined, 
  BankOutlined, 
  WalletOutlined,
  CheckCircleOutlined,
  AlipayCircleOutlined,
  WechatOutlined,
  QrcodeOutlined
} from '@ant-design/icons';
import { useLocation, useNavigate } from 'react-router-dom';
import { PaymentService, PaymentRequest } from '../services/paymentService';
import { OrderService } from '../services/orderService';

const { Title, Text, Paragraph } = Typography;
const { Step } = Steps;

interface PaymentPageState {
  orderId: string;
  amount: number;
}

// 模拟用户ID
const MOCK_USER_ID = 1;

// 模拟二维码图片
const MOCK_ALIPAY_QR_CODE = 'https://via.placeholder.com/300x300?text=支付宝支付';
const MOCK_WECHAT_QR_CODE = 'https://via.placeholder.com/300x300?text=微信支付';

const PaymentPage: React.FC = () => {
  const location = useLocation();
  const navigate = useNavigate();
  
  // 从路由状态中获取数据
  const state = location.state as PaymentPageState;
  
  // 如果没有传递状态，重定向到首页
  useEffect(() => {
    if (!state || !state.orderId) {
      navigate('/');
      message.error('支付信息不完整');
    }
  }, [state, navigate]);
  
  // 支付状态
  const [paymentMethod, setPaymentMethod] = useState<string>('credit_card');
  const [paymentStep, setPaymentStep] = useState<number>(0);
  const [processing, setProcessing] = useState<boolean>(false);
  const [selectedCardId, setSelectedCardId] = useState<number | undefined>(undefined);
  const [selectedInstallmentPlanId, setSelectedInstallmentPlanId] = useState<number | undefined>(undefined);
  
  // 二维码模态框状态
  const [qrCodeVisible, setQrCodeVisible] = useState<boolean>(false);
  const [qrCodeImage, setQrCodeImage] = useState<string>('');
  const [qrCodeTitle, setQrCodeTitle] = useState<string>('');
  
  // 处理支付方式选择
  const handlePaymentMethodChange = (e: any) => {
    setPaymentMethod(e.target.value);
  };
  
  // 处理支付提交
  const handlePaymentSubmit = async () => {
    if (!state) return;
    
    // 对于支付宝和微信支付，显示二维码
    if (paymentMethod === 'alipay' || paymentMethod === 'wechat') {
      try {
        setProcessing(true);
        
        let qrCodeResponse;
        if (paymentMethod === 'alipay') {
          // 获取支付宝支付二维码
          qrCodeResponse = await PaymentService.getAlipayQRCode(
            MOCK_USER_ID, 
            parseInt(state.orderId), 
            state.amount
          );
          setQrCodeTitle('支付宝支付');
        } else {
          // 获取微信支付二维码
          qrCodeResponse = await PaymentService.getWechatPayQRCode(
            MOCK_USER_ID, 
            parseInt(state.orderId), 
            state.amount
          );
          setQrCodeTitle('微信支付');
        }
        
        // 设置二维码图片
        setQrCodeImage(qrCodeResponse.qrCodeUrl || (paymentMethod === 'alipay' ? MOCK_ALIPAY_QR_CODE : MOCK_WECHAT_QR_CODE));
        setQrCodeVisible(true);
      } catch (error) {
        console.error('获取支付二维码失败:', error);
        message.error('获取支付二维码失败，请稍后重试');
      } finally {
        setProcessing(false);
      }
      return;
    }
    
    try {
      setProcessing(true);
      
      // 构建支付请求
      const paymentRequest: PaymentRequest = {
        orderId: parseInt(state.orderId),
        paymentMethod,
        amount: state.amount,
        creditCardId: selectedCardId,
        installmentPlanId: selectedInstallmentPlanId
      };
      
      // 调用支付API
      // 实际项目中应该调用PaymentService.payOrder
      // const response = await PaymentService.payOrder(MOCK_USER_ID, paymentRequest);
      
      // 模拟支付处理
      await new Promise(resolve => setTimeout(resolve, 2000));
      
      // 调用订单支付API
      // await OrderService.payOrder(parseInt(state.orderId), MOCK_USER_ID);
      
      // 更新支付状态
      setPaymentStep(1);
      message.success('支付成功！');
    } catch (error) {
      console.error('支付失败:', error);
      message.error('支付失败，请稍后重试');
    } finally {
      setProcessing(false);
    }
  };
  
  // 处理二维码支付完成
  const handleQrCodePaymentComplete = async () => {
    setQrCodeVisible(false);
    
    try {
      setProcessing(true);
      
      // 模拟支付处理
      await new Promise(resolve => setTimeout(resolve, 1000));
      
      // 更新支付状态
      setPaymentStep(1);
      message.success('支付成功！');
    } catch (error) {
      console.error('支付失败:', error);
      message.error('支付失败，请稍后重试');
    } finally {
      setProcessing(false);
    }
  };
  
  // 处理返回订单列表
  const handleBackToOrders = () => {
    navigate('/orders');
  };
  
  // 处理继续购物
  const handleContinueShopping = () => {
    navigate('/');
  };
  
  // 渲染支付方式
  const renderPaymentMethods = () => {
    return (
      <div>
        <Title level={4}>支付方式</Title>
        <Radio.Group value={paymentMethod} onChange={handlePaymentMethodChange}>
          <Space direction="vertical" style={{ width: '100%' }}>
            <Radio value="credit_card">
              <Space>
                <CreditCardOutlined />
                信用卡支付
              </Space>
            </Radio>
            <Radio value="alipay">
              <Space>
                <AlipayCircleOutlined style={{ color: '#1677FF' }} />
                支付宝支付
              </Space>
            </Radio>
            <Radio value="wechat">
              <Space>
                <WechatOutlined style={{ color: '#07C160' }} />
                微信支付
              </Space>
            </Radio>
            <Radio value="bank_transfer">
              <Space>
                <BankOutlined />
                银行转账
              </Space>
            </Radio>
            <Radio value="balance">
              <Space>
                <WalletOutlined />
                余额支付
              </Space>
            </Radio>
          </Space>
        </Radio.Group>
      </div>
    );
  };
  
  if (!state) {
    return null;
  }
  
  return (
    <div style={{ padding: '20px', maxWidth: 800, margin: '0 auto' }}>
      <Card>
        <Steps current={paymentStep} style={{ marginBottom: 40 }}>
          <Step title="选择支付方式" />
          <Step title="支付完成" />
        </Steps>
        
        {paymentStep === 0 && (
          <div>
            <div style={{ marginBottom: 24 }}>
              <Title level={4}>订单信息</Title>
              <div style={{ background: '#f5f5f5', padding: 16, borderRadius: 4 }}>
                <div style={{ display: 'flex', justifyContent: 'space-between', marginBottom: 8 }}>
                  <Text>订单编号:</Text>
                  <Text>{state.orderId}</Text>
                </div>
                <div style={{ display: 'flex', justifyContent: 'space-between' }}>
                  <Text>应付金额:</Text>
                  <Text strong style={{ color: '#ff4d4f', fontSize: 16 }}>
                    ¥{state.amount.toFixed(2)}
                  </Text>
                </div>
              </div>
            </div>
            
            {renderPaymentMethods()}
            
            <div style={{ marginTop: 24, textAlign: 'center' }}>
              <Button
                type="primary"
                size="large"
                onClick={handlePaymentSubmit}
                loading={processing}
              >
                确认支付
              </Button>
            </div>
          </div>
        )}
        
        {paymentStep === 1 && (
          <Result
            status="success"
            title="支付成功！"
            subTitle={`订单号: ${state.orderId} 已支付完成，感谢您的购买！`}
            extra={[
              <Button type="primary" key="orders" onClick={handleBackToOrders}>
                查看订单
              </Button>,
              <Button key="buy" onClick={handleContinueShopping}>继续购物</Button>,
            ]}
          />
        )}
      </Card>
      
      {/* 二维码支付模态框 */}
      <Modal
        title={qrCodeTitle}
        open={qrCodeVisible}
        onCancel={() => setQrCodeVisible(false)}
        footer={[
          <Button key="cancel" onClick={() => setQrCodeVisible(false)}>
            取消
          </Button>,
          <Button key="complete" type="primary" onClick={handleQrCodePaymentComplete}>
            支付完成
          </Button>,
        ]}
      >
        <div style={{ textAlign: 'center' }}>
          <Paragraph>
            请使用{paymentMethod === 'alipay' ? '支付宝' : '微信'}扫描下方二维码进行支付
          </Paragraph>
          <div style={{ margin: '20px 0' }}>
            <Image
              src={qrCodeImage}
              alt={qrCodeTitle}
              width={200}
              height={200}
              style={{ margin: '0 auto' }}
            />
          </div>
          <div style={{ background: '#f5f5f5', padding: 16, borderRadius: 4, textAlign: 'left' }}>
            <div style={{ marginBottom: 8 }}>
              <Text>订单编号: {state?.orderId}</Text>
            </div>
            <div>
              <Text>应付金额: </Text>
              <Text strong style={{ color: '#ff4d4f' }}>
                ¥{state?.amount.toFixed(2)}
              </Text>
            </div>
          </div>
        </div>
      </Modal>
    </div>
  );
};

export default PaymentPage;
