// 订单状态枚举
export enum OrderStatus {
  PENDING_PAYMENT = 'PENDING_PAYMENT',
  PAID = 'PAID',
  SHIPPED = 'SHIPPED',
  COMPLETED = 'COMPLETED',
  CANCELLED = 'CANCELLED'
}

// 支付状态枚举
export enum PaymentStatus {
  UNPAID = 'UNPAID',
  PAID = 'PAID',
  PARTIAL_PAID = 'PARTIAL_PAID',
  REFUNDED = 'REFUNDED'
}

// 支付方式枚举
export enum PaymentMethod {
  ALIPAY = 'ALIPAY',
  WECHAT = 'WECHAT',
  UNION_PAY = 'UNION_PAY',
  CREDIT_CARD = 'CREDIT_CARD'
}

// 分期方案接口
export interface InstallmentPlan {
  id: number;
  name: string;
  periods: number;
  interestRate: number;
  minAmount: number;
  maxAmount: number;
  description: string;
}

// 用户地址接口
export interface UserAddress {
  id: number;
  userId: number;
  receiverName: string;
  receiverPhone: string;
  province: string;
  city: string;
  district: string;
  detailAddress: string;
  isDefault: boolean;
  fullAddress?: string;
}

// 订单商品接口
export interface OrderItem {
  id: number;
  orderId: number;
  productId: number;
  productName: string;
  productImage: string;
  price: number;
  quantity: number;
  totalPrice: number;
}

// 订单接口
export interface Order {
  id: number;
  orderNo: string;
  userId: number;
  orderStatus: OrderStatus;
  paymentStatus: PaymentStatus;
  paymentMethod: PaymentMethod;
  totalAmount: number;
  actualAmount: number;
  discountAmount: number;
  shippingFee: number;
  receiverName: string;
  receiverPhone: string;
  receiverAddress: string;
  isInstallment: boolean;
  installmentPlanId?: number;
  installmentCount?: number;
  monthlyAmount?: number;
  orderRemark?: string;
  createdAt: string;
  updatedAt: string;
  paidAt?: string;
  shippedAt?: string;
  completedAt?: string;
  orderItems?: OrderItem[];
  installmentPlan?: InstallmentPlan;
}

// 创建订单请求接口
export interface CreateOrderRequest {
  cartItemIds: number[];
  addressId: number;
  paymentMethod: PaymentMethod;
  isInstallment: boolean;
  installmentPlanId?: number;
  orderRemark?: string;
}

// 创建订单响应接口
export interface CreateOrderResponse {
  orderId: number;
  orderNo: string;
  totalAmount: number;
  actualAmount: number;
  paymentUrl?: string;
}

// 添加地址请求接口
export interface AddAddressRequest {
  receiverName: string;
  receiverPhone: string;
  province: string;
  city: string;
  district: string;
  detailAddress: string;
  isDefault: boolean;
}

// API响应基础接口
export interface ApiResponse<T = any> {
  code: number;
  message: string;
  data: T;
}

// 分页响应接口
export interface PageResponse<T> {
  content: T[];
  totalElements: number;
  totalPages: number;
  size: number;
  number: number;
}
