import React, { useState, useEffect } from 'react';
import { 
  Card, 
  Form, 
  Button, 
  Radio, 
  Input, 
  Space, 
  Divider, 
  message, 
  Row,
  Col,
  Typography,
  Spin
} from 'antd';
import { useNavigate, useLocation } from 'react-router-dom';
import { EnvironmentOutlined } from '@ant-design/icons';
import { PaymentMethod, InstallmentPlan } from '../types/order';
import { CartItem } from '../types';
import { installmentApi } from '../services/orderApi';

const { Title, Text } = Typography;
const { TextArea } = Input;

const CheckoutPage: React.FC = () => {
  const navigate = useNavigate();
  const location = useLocation();
  const { selectedItems } = location.state || { selectedItems: [] };
  
  const [form] = Form.useForm();
  const [loading, setLoading] = useState(false);
  const [submitting, setSubmitting] = useState(false);
  const [installmentPlans, setInstallmentPlans] = useState<InstallmentPlan[]>([]);
  const [selectedInstallment, setSelectedInstallment] = useState<number | null>(null);
  const [paymentMode, setPaymentMode] = useState<'full' | 'installment'>('full');

  // 计算总金额
  const totalAmount = selectedItems.reduce((sum: number, item: CartItem) => 
    sum + item.product.price * item.quantity, 0
  );
  const shippingFee = 0; // 假设免运费
  const finalAmount = totalAmount + shippingFee;

  useEffect(() => {
    if (!selectedItems || selectedItems.length === 0) {
      message.error('请先选择要结算的商品');
      navigate('/cart');
      return;
    }
    loadInstallmentPlans();
  }, [selectedItems, navigate]);

  // 加载分期方案
  const loadInstallmentPlans = async () => {
    try {
      const response = await installmentApi.getPlansByAmount(finalAmount);
      if (response.code === 200) {
        setInstallmentPlans(response.data || []);
      }
    } catch (error) {
      console.error('加载分期方案失败:', error);
      // 使用模拟数据
      setInstallmentPlans([
        {
          id: 1,
          planName: '3期免息',
          installmentCount: 3,
          interestRate: 0,
          minAmount: 500,
          maxAmount: 50000,
          status: 1
        },
        {
          id: 2,
          planName: '6期免息',
          installmentCount: 6,
          interestRate: 0,
          minAmount: 1000,
          maxAmount: 30000,
          status: 1
        },
        {
          id: 3,
          planName: '12期免息',
          installmentCount: 12,
          interestRate: 0,
          minAmount: 2000,
          maxAmount: 20000,
          status: 1
        },
        {
          id: 4,
          planName: '24期低息',
          installmentCount: 24,
          interestRate: 0.5,
          minAmount: 5000,
          maxAmount: 50000,
          status: 1
        }
      ]);
    }
  };

  // 计算月供
  const calculateMonthlyPayment = (plan: InstallmentPlan) => {
    if (plan.interestRate === 0) {
      return finalAmount / plan.installmentCount;
    }
    // 简化的有息计算
    const monthlyRate = plan.interestRate / 100;
    const totalWithInterest = finalAmount * (1 + monthlyRate * plan.installmentCount);
    return totalWithInterest / plan.installmentCount;
  };

  // 提交订单
  const handleSubmit = async (values: any) => {
    try {
      setSubmitting(true);
      
      // 模拟订单创建
      await new Promise(resolve => setTimeout(resolve, 2000));
      
      message.success('订单创建成功');
      
      // 跳转到订单页面
      navigate('/orders');
    } catch (error) {
      message.error('创建订单失败');
    } finally {
      setSubmitting(false);
    }
  };

  // 渲染分期方式选择组件
  const renderInstallmentOptions = () => {
    return (
      <Card title="分期方式" style={{ marginBottom: '16px' }}>
        <Text type="secondary" style={{ marginBottom: '16px', display: 'block' }}>
          仅支付金额分含与分期，积分不参与分期
        </Text>
        
        <div style={{ marginBottom: '16px' }}>
          {/* 全款支付选项 */}
          <Button
            size="large"
            style={{
              width: '100%',
              height: '60px',
              marginBottom: '12px',
              display: 'flex',
              justifyContent: 'space-between',
              alignItems: 'center',
              backgroundColor: paymentMode === 'full' ? '#e7f4ff' : '#ffffff',
              borderColor: paymentMode === 'full' ? '#1890ff' : '#d9d9d9',
              color: paymentMode === 'full' ? '#1890ff' : '#000000'
            }}
            onClick={() => {
              setPaymentMode('full');
              setSelectedInstallment(null);
            }}
          >
            <span>{finalAmount.toFixed(2)}元・全款支付</span>
          </Button>

          {/* 分期支付选项 */}
          <div style={{ display: 'grid', gridTemplateColumns: '1fr 1fr', gap: '12px' }}>
            {installmentPlans
              .filter(plan => finalAmount >= plan.minAmount && finalAmount <= plan.maxAmount)
              .map(plan => {
                const monthlyAmount = calculateMonthlyPayment(plan);
                const isSelected = paymentMode === 'installment' && selectedInstallment === plan.id;
                
                return (
                  <Button
                    key={plan.id}
                    size="large"
                    style={{
                      height: '60px',
                      display: 'flex',
                      flexDirection: 'column',
                      justifyContent: 'center',
                      alignItems: 'center',
                      backgroundColor: isSelected ? '#ffe7e7' : '#ffffff',
                      borderColor: isSelected ? '#ff4757' : '#d9d9d9',
                      color: isSelected ? '#ff4757' : '#000000'
                    }}
                    onClick={() => {
                      setPaymentMode('installment');
                      setSelectedInstallment(plan.id);
                    }}
                  >
                    <div style={{ fontSize: '14px', fontWeight: 'bold' }}>
                      {monthlyAmount.toFixed(2)}元×{plan.installmentCount}期・{plan.interestRate === 0 ? '免息' : '低息'}
                    </div>
                  </Button>
                );
              })
            }
          </div>

          {/* 全款支付按钮 */}
          {paymentMode === 'installment' && (
            <Button
              size="large"
              style={{
                width: '100%',
                height: '50px',
                marginTop: '12px',
                backgroundColor: '#f5f5f5',
                borderColor: '#d9d9d9',
                color: '#666666'
              }}
              onClick={() => {
                setPaymentMode('full');
                setSelectedInstallment(null);
              }}
            >
              {finalAmount.toFixed(2)}元・全款支付
            </Button>
          )}
        </div>

        <Text type="secondary" style={{ fontSize: '12px' }}>
          *免息分期仅支持招商银行信用卡支付，请确保您卡片的可用额度大于订单总金额。
        </Text>
      </Card>
    );
  };

  if (loading) {
    return (
      <div style={{ padding: '50px', textAlign: 'center' }}>
        <Spin size="large" />
      </div>
    );
  }

  return (
    <div style={{ padding: '20px', backgroundColor: '#f5f5f5', minHeight: '100vh' }}>
      <div style={{ maxWidth: '1200px', margin: '0 auto' }}>
        <Title level={2}>确认订单</Title>
        
        <Form form={form} onFinish={handleSubmit} layout="vertical">
          <Row gutter={24}>
            {/* 左侧主要内容 */}
            <Col span={16}>
              {/* 收货地址 */}
              <Card 
                title={
                  <Space>
                    <EnvironmentOutlined />
                    收货地址
                  </Space>
                }
                style={{ marginBottom: '16px' }}
              >
                <Form.Item name="receiverName" label="收货人" rules={[{ required: true, message: '请输入收货人' }]}>
                  <Input placeholder="请输入收货人姓名" />
                </Form.Item>
                <Form.Item name="receiverPhone" label="联系电话" rules={[{ required: true, message: '请输入联系电话' }]}>
                  <Input placeholder="请输入联系电话" />
                </Form.Item>
                <Form.Item name="address" label="详细地址" rules={[{ required: true, message: '请输入详细地址' }]}>
                  <TextArea rows={2} placeholder="请输入详细地址" />
                </Form.Item>
              </Card>

              {/* 商品信息 */}
              <Card title="商品信息" style={{ marginBottom: '16px' }}>
                {selectedItems.map((item: CartItem) => (
                  <div key={item.id} style={{ display: 'flex', padding: '12px 0', borderBottom: '1px solid #f0f0f0' }}>
                    <img 
                      src={item.product.imageUrl || '/placeholder.jpg'} 
                      alt={item.product.name}
                      style={{ width: '80px', height: '80px', objectFit: 'cover', marginRight: '12px' }}
                    />
                    <div style={{ flex: 1 }}>
                      <Text strong>{item.product.name}</Text>
                      <br />
                      <Text type="secondary">数量: {item.quantity}</Text>
                      <br />
                      <Text type="danger">¥{item.product.price.toFixed(2)}</Text>
                    </div>
                    <div>
                      <Text strong>¥{(item.product.price * item.quantity).toFixed(2)}</Text>
                    </div>
                  </div>
                ))}
              </Card>

              {/* 支付方式 */}
              {renderInstallmentOptions()}

              {/* 订单备注 */}
              <Card title="订单备注">
                <Form.Item name="orderRemark">
                  <TextArea 
                    rows={3} 
                    placeholder="请输入订单备注（可选）"
                    maxLength={200}
                  />
                </Form.Item>
              </Card>
            </Col>

            {/* 右侧订单汇总 */}
            <Col span={8}>
              <Card title="订单汇总" style={{ position: 'sticky', top: '20px' }}>
                <div style={{ marginBottom: '12px' }}>
                  <Space style={{ width: '100%', justifyContent: 'space-between' }}>
                    <Text>商品总额:</Text>
                    <Text>¥{totalAmount.toFixed(2)}</Text>
                  </Space>
                </div>
                <div style={{ marginBottom: '12px' }}>
                  <Space style={{ width: '100%', justifyContent: 'space-between' }}>
                    <Text>运费:</Text>
                    <Text>¥{shippingFee.toFixed(2)}</Text>
                  </Space>
                </div>
                
                {/* 分期信息显示 */}
                {paymentMode === 'installment' && selectedInstallment && (
                  <>
                    <Divider />
                    {(() => {
                      const selectedPlan = installmentPlans.find(p => p.id === selectedInstallment);
                      if (selectedPlan) {
                        const monthlyAmount = calculateMonthlyPayment(selectedPlan);
                        return (
                          <div style={{ marginBottom: '12px' }}>
                            <Space style={{ width: '100%', justifyContent: 'space-between' }}>
                              <Text strong>分期方案:</Text>
                              <Text strong style={{ color: '#ff4d4f' }}>
                                {selectedPlan.planName}
                              </Text>
                            </Space>
                            <div style={{ marginTop: '8px' }}>
                              <Space style={{ width: '100%', justifyContent: 'space-between' }}>
                                <Text>每期金额:</Text>
                                <Text strong style={{ color: '#ff4d4f' }}>
                                  ¥{monthlyAmount.toFixed(2)}
                                </Text>
                              </Space>
                            </div>
                          </div>
                        );
                      }
                      return null;
                    })()}
                  </>
                )}
                
                <Divider />
                <div style={{ marginBottom: '20px' }}>
                  <Space style={{ width: '100%', justifyContent: 'space-between' }}>
                    <Text strong>
                      {paymentMode === 'installment' ? '订单总额:' : '实付款:'}
                    </Text>
                    <Text strong style={{ fontSize: '18px', color: '#ff4d4f' }}>
                      ¥{finalAmount.toFixed(2)}
                    </Text>
                  </Space>
                </div>

                <Button 
                  type="primary" 
                  size="large" 
                  htmlType="submit"
                  loading={submitting}
                  block
                  style={{ height: '50px', fontSize: '16px' }}
                >
                  {paymentMode === 'installment' ? '确认分期订单' : '提交订单'}
                </Button>
              </Card>
            </Col>
          </Row>
        </Form>
      </div>
    </div>
  );
};

export default CheckoutPage;
