import React, { useState, useEffect } from 'react';
import { Input, Button, Tag, Tooltip, Space, Divider } from 'antd';
import { SearchOutlined, QuestionCircleOutlined } from '@ant-design/icons';
import './NaturalLanguageInput.css';

interface NaturalLanguageInputProps {
  onSearch: (query: string) => void;
  loading?: boolean;
  placeholder?: string;
}

// Â∏∏ËßÅÈóÆÈ¢òÂàóË°®
const COMMON_QUESTIONS = [
  { text: 'Êü•ËØ¢ÊàëÁöÑËÆ¢Âçï', category: 'order' },
  { text: 'ÊàëÁöÑÁâ©ÊµÅ‰ø°ÊÅØ', category: 'logistics' },
  { text: 'ÈÄÄÊç¢Ë¥ßÊîøÁ≠ñ', category: 'return' },
  { text: 'ÁßØÂàÜÊü•ËØ¢', category: 'points' },
  { text: 'ÂàÜÊúü‰ªòÊ¨æ', category: 'payment' }
];

// ËØ≠‰πâÊü•ËØ¢Á§∫‰æã
const SEMANTIC_EXAMPLES = [
  'Êü•ËØ¢ÊúÄËøë‰∏âÂ§©ÁöÑËÆ¢Âçï',
  'Êò®Â§©‰π∞ÁöÑÊâãÊú∫‰ªÄ‰πàÊó∂ÂÄôÂèëË¥ß',
  'ÊÄé‰πàÁî≥ËØ∑ÈÄÄÊ¨æ',
  'ÊàëÁöÑÁßØÂàÜÂèØ‰ª•ÂÖëÊç¢‰ªÄ‰πà',
  'Ëøô‰∏™ÂïÜÂìÅÂèØ‰ª•ÂàÜÊúüÂêó'
];

const NaturalLanguageInput: React.FC<NaturalLanguageInputProps> = ({
  onSearch,
  loading = false,
  placeholder = 'ÊÇ®ÂèØ‰ª•Áõ¥Êé•ËØ¢ÈóÆÂ¶Ç"Êü•ËØ¢ÊàëÊúÄËøëÁöÑËÆ¢Âçï"Êàñ"ÊàëÊÉ≥ÈÄÄË¥ß"'
}) => {
  const [value, setValue] = useState<string>('');
  const [showExamples, setShowExamples] = useState<boolean>(false);
  const [suggestions, setSuggestions] = useState<string[]>([]);

  // ÂΩìËæìÂÖ•ÂèòÂåñÊó∂Êèê‰æõÊô∫ËÉΩÂª∫ËÆÆ
  useEffect(() => {
    if (value.length > 1) {
      // ÁÆÄÂçïÁöÑÂÖ≥ÈîÆËØçÂåπÈÖçÊù•Êèê‰æõÂª∫ËÆÆ
      const matchedSuggestions = SEMANTIC_EXAMPLES.filter(example => 
        example.toLowerCase().includes(value.toLowerCase())
      ).slice(0, 3);
      
      setSuggestions(matchedSuggestions);
    } else {
      setSuggestions([]);
    }
  }, [value]);

  // Â§ÑÁêÜËæìÂÖ•ÂèòÂåñ
  const handleInputChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    setValue(e.target.value);
  };

  // Â§ÑÁêÜÊêúÁ¥¢
  const handleSearch = () => {
    if (value.trim()) {
      onSearch(value.trim());
      setValue('');
    }
  };

  // Â§ÑÁêÜÊåâÈîÆ‰∫ã‰ª∂
  const handleKeyPress = (e: React.KeyboardEvent<HTMLInputElement>) => {
    if (e.key === 'Enter') {
      handleSearch();
    }
  };

  // Â§ÑÁêÜÂø´Êç∑ÈóÆÈ¢òÁÇπÂáª
  const handleQuestionClick = (question: string) => {
    onSearch(question);
  };

  // Â§ÑÁêÜÂª∫ËÆÆÁÇπÂáª
  const handleSuggestionClick = (suggestion: string) => {
    onSearch(suggestion);
    setValue('');
    setSuggestions([]);
  };

  return (
    <div className="natural-language-input">
      <div className="input-container">
        <Input
          value={value}
          onChange={handleInputChange}
          onKeyPress={handleKeyPress}
          placeholder={placeholder}
          disabled={loading}
          suffix={
            <Tooltip title="ÊÇ®ÂèØ‰ª•Áî®Ëá™ÁÑ∂ËØ≠Ë®ÄÊèêÈóÆÔºå‰æãÂ¶Ç'Êü•ËØ¢ÊàëÊúÄËøëÁöÑËÆ¢Âçï'">
              <QuestionCircleOutlined
                style={{ color: 'rgba(0,0,0,.45)' }}
                onClick={() => setShowExamples(!showExamples)}
              />
            </Tooltip>
          }
        />
        <Button
          type="primary"
          icon={<SearchOutlined />}
          onClick={handleSearch}
          loading={loading}
          disabled={!value.trim()}
        >
          ÊêúÁ¥¢
        </Button>
      </div>

      {/* Êô∫ËÉΩÂª∫ËÆÆ */}
      {suggestions.length > 0 && (
        <div className="suggestions">
          <div className="suggestions-title">ÊÇ®ÊòØÂê¶ÊÉ≥ÈóÆÔºö</div>
          <Space>
            {suggestions.map((suggestion, index) => (
              <Tag
                key={index}
                color="blue"
                className="suggestion-tag"
                onClick={() => handleSuggestionClick(suggestion)}
              >
                {suggestion}
              </Tag>
            ))}
          </Space>
        </div>
      )}

      {/* Á§∫‰æãÂíåÂ∏∏ËßÅÈóÆÈ¢ò */}
      {showExamples && (
        <div className="examples-container">
          <Divider orientation="left">Â∏∏ËßÅÈóÆÈ¢ò</Divider>
          <div className="common-questions">
            {COMMON_QUESTIONS.map((question, index) => (
              <Button
                key={index}
                type="default"
                size="small"
                className={`question-button ${question.category}`}
                onClick={() => handleQuestionClick(question.text)}
              >
                {question.text}
              </Button>
            ))}
          </div>
          
          <Divider orientation="left">ËØ≠‰πâÊü•ËØ¢Á§∫‰æã</Divider>
          <div className="semantic-examples">
            {SEMANTIC_EXAMPLES.map((example, index) => (
              <div key={index} className="example-item">
                <span className="example-icon">üí°</span>
                <span className="example-text">{example}</span>
              </div>
            ))}
          </div>
        </div>
      )}
    </div>
  );
};

export default NaturalLanguageInput;
