import React, { useEffect, useState } from 'react';
import { 
  Card, 
  Row, 
  Col, 
  Pagination, 
  Spin, 
  Alert, 
  Select, 
  Button, 
  Space, 
  Typography,
  Image,
  Tag,
  Empty,
  Drawer,
  Checkbox,
  Radio,
  Slider,
  Divider,
  Rate,
  Badge,
  Tooltip,
  InputNumber
} from 'antd';
import { 
  SearchOutlined, 
  FilterOutlined, 
  ShoppingCartOutlined, 
  EyeOutlined, 
  HeartOutlined,
  SortAscendingOutlined,
  HistoryOutlined
} from '@ant-design/icons';
import { useDispatch, useSelector } from 'react-redux';
import { useSearchParams, useNavigate } from 'react-router-dom';
import { RootState, AppDispatch } from '../store';
import { searchProducts, resetSearch } from '../store/slices/productSlice';
import { fetchAllCategories } from '../store/slices/categorySlice';
import { Product, ProductSearchParams } from '../types';

const { Title, Text, Paragraph } = Typography;
const { Option } = Select;
const { Group: CheckboxGroup } = Checkbox;

// 价格区间选项
const PRICE_RANGES = [
  { label: '0-100元', value: [0, 100] },
  { label: '100-500元', value: [100, 500] },
  { label: '500-1000元', value: [500, 1000] },
  { label: '1000-5000元', value: [1000, 5000] },
  { label: '5000元以上', value: [5000, 999999] }
];

// 扩展ProductSearchParams类型
interface ExtendedProductSearchParams {
  keyword: string;
  current?: number;
  size?: number;
  sortBy?: string;
  categoryId?: number;
  minPrice?: number;
  maxPrice?: number;
  minRating?: number;
}

const SearchResults: React.FC = () => {
  const [searchParams, setSearchParams] = useSearchParams();
  const navigate = useNavigate();
  const dispatch = useDispatch<AppDispatch>();
  
  const { 
    searchResults, 
    searchTotal, 
    searchCurrent, 
    searchLoading, 
    searchError,
    lastSearchKeyword 
  } = useSelector((state: RootState) => state.products);
  
  const { allCategories } = useSelector((state: RootState) => state.categories);
  
  const [filterDrawerVisible, setFilterDrawerVisible] = useState(false);
  const [selectedCategories, setSelectedCategories] = useState<number[]>([]);
  const [priceRange, setPriceRange] = useState<[number, number] | null>(null);
  const [customPriceRange, setCustomPriceRange] = useState<[number | null, number | null]>([null, null]);
  const [selectedRating, setSelectedRating] = useState<number | null>(null);
  const [sortBy, setSortBy] = useState<string>('relevance');
  
  // 获取URL参数
  const keyword = searchParams.get('keyword') || '';
  const categoryId = searchParams.get('category');
  const minPrice = searchParams.get('minPrice');
  const maxPrice = searchParams.get('maxPrice');
  const rating = searchParams.get('rating');
  const sort = searchParams.get('sort');
  
  // 初始化筛选条件
  useEffect(() => {
    // 只在组件挂载时或URL参数变化时执行一次
    if (categoryId) {
      setSelectedCategories([parseInt(categoryId)]);
    }
    
    if (minPrice && maxPrice) {
      setPriceRange([parseInt(minPrice), parseInt(maxPrice)]);
      setCustomPriceRange([parseInt(minPrice), parseInt(maxPrice)]);
    }
    
    if (rating) {
      setSelectedRating(parseInt(rating));
    }
    
    if (sort) {
      setSortBy(sort);
    }
  }, [categoryId, minPrice, maxPrice, rating, sort]); // 只依赖URL参数
  
  // 更新URL参数
  const updateSearchParams = React.useCallback((params: any) => {
    const newParams = new URLSearchParams();
    
    // 保留关键词
    newParams.set('keyword', keyword);
    
    // 添加分类
    if (params.categoryId) {
      newParams.set('category', params.categoryId.toString());
    }
    
    // 添加价格
    if (params.minPrice) {
      newParams.set('minPrice', params.minPrice.toString());
    }
    if (params.maxPrice) {
      newParams.set('maxPrice', params.maxPrice.toString());
    }
    
    // 添加评分
    if (params.minRating) {
      newParams.set('rating', params.minRating.toString());
    }
    
    // 添加排序
    if (params.sortBy) {
      newParams.set('sort', params.sortBy);
    }
    
    // 添加页码
    if (params.current && params.current > 1) {
      newParams.set('page', params.current.toString());
    }
    
    // 更新URL，不刷新页面
    setSearchParams(newParams);
  }, [keyword, setSearchParams]); // 只依赖keyword和setSearchParams

useEffect(() => {
  // 只在关键词变化时执行初始搜索
  if (keyword && keyword !== lastSearchKeyword) {
    // 获取所有分类
    dispatch(fetchAllCategories());
    
    // 执行搜索，带上筛选参数
    dispatch(searchProducts({ 
      keyword,
      current: 1,
      size: 12,
      categoryId: categoryId ? parseInt(categoryId) : undefined,
      minPrice: minPrice ? parseInt(minPrice) : undefined,
      maxPrice: maxPrice ? parseInt(maxPrice) : undefined,
      minRating: rating ? parseInt(rating) : undefined,
      sortBy: sort || undefined
    }));
  }
  
  // 组件卸载时重置搜索结果
  return () => {
    dispatch(resetSearch());
  };
}, [dispatch, keyword, lastSearchKeyword, categoryId, minPrice, maxPrice, rating, sort]); // 添加所有URL参数作为依赖
  
  // 处理分页变化
  const handlePageChange = (page: number, pageSize?: number) => {
    // 使用dispatch更新Redux中的当前页码，而不是直接调用executeSearch
    dispatch(searchProducts({ 
      keyword,
      current: page, 
      size: pageSize || 12,
      categoryId: selectedCategories.length === 1 ? selectedCategories[0] : undefined,
      minPrice: priceRange ? priceRange[0] : undefined,
      maxPrice: priceRange ? priceRange[1] : undefined,
      minRating: selectedRating ?? undefined,
      sortBy: sortBy !== 'relevance' ? sortBy : undefined
    }));
    
    // 更新URL参数
    const newParams = new URLSearchParams(searchParams);
    newParams.set('page', page.toString());
    if (pageSize) {
      newParams.set('size', pageSize.toString());
    }
    setSearchParams(newParams);
  };
  
  // 处理排序变化
  const handleSortChange = (value: string) => {
    // 先更新本地状态
    setSortBy(value);
    
    // 直接使用dispatch调用action，而不是使用setTimeout和executeSearch
    dispatch(searchProducts({ 
      keyword,
      current: 1, // 排序变化时重置为第一页
      size: 12,
      categoryId: selectedCategories.length === 1 ? selectedCategories[0] : undefined,
      minPrice: priceRange ? priceRange[0] : undefined,
      maxPrice: priceRange ? priceRange[1] : undefined,
      minRating: selectedRating ?? undefined,
      sortBy: value !== 'relevance' ? value : undefined
    }));
    
    // 更新URL参数
    const newParams = new URLSearchParams(searchParams);
    if (value !== 'relevance') {
      newParams.set('sort', value);
    } else {
      newParams.delete('sort');
    }
    newParams.set('page', '1'); // 重置页码
    setSearchParams(newParams);
  };
  
  // 处理分类选择
  const handleCategoryChange = (values: number[]) => {
    setSelectedCategories(values);
    // 不需要在这里立即执行搜索，用户需要点击"应用筛选"按钮
  };
  
  // 处理价格区间选择
  const handlePriceRangeChange = (range: [number, number]) => {
    setPriceRange(range);
    setCustomPriceRange([range[0], range[1]]);
    // 不需要在这里立即执行搜索，用户需要点击"应用筛选"按钮
  };
  
  // 处理自定义价格区间
  const handleCustomPriceChange = (index: number, value: number | null) => {
    const newRange = [...customPriceRange];
    newRange[index] = value;
    setCustomPriceRange(newRange as [number | null, number | null]);
    
    // 只在两个值都有效时更新价格区间
    if (newRange[0] !== null && newRange[1] !== null) {
      setPriceRange([newRange[0], newRange[1]] as [number, number]);
    }
    // 不需要在这里立即执行搜索，用户需要点击"应用筛选"按钮
  };
  
  // 处理评分选择
  const handleRatingChange = (value: number) => {
    // 如果选择的是当前评分，则清除评分筛选
    const newRating = value === selectedRating ? null : value;
    setSelectedRating(newRating);
    
    // 不需要在这里立即执行搜索，用户需要点击"应用筛选"按钮
  };
  
  // 应用筛选条件
  const applyFilters = () => {
    setFilterDrawerVisible(false);
    
    // 直接使用dispatch调用action，而不是使用setTimeout和executeSearch
    dispatch(searchProducts({ 
      keyword,
      current: 1, // 筛选条件变化时重置为第一页
      size: 12,
      categoryId: selectedCategories.length === 1 ? selectedCategories[0] : undefined,
      minPrice: priceRange ? priceRange[0] : undefined,
      maxPrice: priceRange ? priceRange[1] : undefined,
      minRating: selectedRating ?? undefined,
      sortBy: sortBy !== 'relevance' ? sortBy : undefined
    }));
    
    // 更新URL参数
    updateSearchParams({
      categoryId: selectedCategories.length === 1 ? selectedCategories[0] : undefined,
      minPrice: priceRange ? priceRange[0] : undefined,
      maxPrice: priceRange ? priceRange[1] : undefined,
      minRating: selectedRating ?? undefined,
      sortBy: sortBy !== 'relevance' ? sortBy : undefined
    });
  };
  
  // 清除所有筛选条件
  const clearAllFilters = () => {
    // 重置所有筛选条件状态
    setSelectedCategories([]);
    setPriceRange(null);
    setCustomPriceRange([null, null]);
    setSelectedRating(null);
    
    // 直接使用dispatch调用action
    dispatch(searchProducts({ 
      keyword,
      current: 1,
      size: 12,
      sortBy: sortBy !== 'relevance' ? sortBy : undefined
    }));
    
    // 更新URL参数，只保留关键词和排序
    const newParams = new URLSearchParams();
    newParams.set('keyword', keyword);
    if (sortBy !== 'relevance') {
      newParams.set('sort', sortBy);
    }
    setSearchParams(newParams);
  };
  
  // 跳转到商品详情页
  const handleProductClick = (productId: number) => {
    navigate(`/product/${productId}`);
  };
  
  // 渲染商品卡片
  const renderProductCard = (product: Product) => (
    <Col xs={24} sm={12} md={8} lg={6} key={product.id}>
      <Badge.Ribbon 
        text={product.salesCount > 100 ? "热销" : ""} 
        color="red" 
        style={{ display: product.salesCount > 100 ? 'block' : 'none' }}
      >
        <Card
          hoverable
          style={{ marginBottom: 16 }}
          cover={
            <div style={{ position: 'relative' }}>
              <Image
                alt={product.name}
                src={product.imageUrl || 'https://via.placeholder.com/300x300?text=暂无图片'}
                height={200}
                style={{ objectFit: 'cover' }}
                preview={false}
                onClick={() => handleProductClick(product.id)}
              />
              <div 
                style={{ 
                  position: 'absolute', 
                  top: 8, 
                  right: 8, 
                  background: 'rgba(255,255,255,0.8)', 
                  borderRadius: '50%', 
                  width: 32, 
                  height: 32, 
                  display: 'flex', 
                  alignItems: 'center', 
                  justifyContent: 'center',
                  cursor: 'pointer'
                }}
              >
                <EyeOutlined style={{ fontSize: 18 }} />
              </div>
            </div>
          }
          onClick={() => handleProductClick(product.id)}
          actions={[
            <Tooltip title="查看详情">
              <Button 
                type="text" 
                icon={<EyeOutlined />} 
                onClick={(e) => {
                  e.stopPropagation();
                  handleProductClick(product.id);
                }}
              />
            </Tooltip>,
            <Tooltip title="加入购物车">
              <Button 
                type="text" 
                icon={<ShoppingCartOutlined />} 
                onClick={(e) => e.stopPropagation()}
              />
            </Tooltip>,
            <Tooltip title="收藏">
              <Button 
                type="text" 
                icon={<HeartOutlined />} 
                onClick={(e) => e.stopPropagation()}
              />
            </Tooltip>
          ]}
        >
          <Card.Meta
            title={
              <div style={{ height: 44, overflow: 'hidden' }}>
                <Text ellipsis={{ tooltip: product.name }}>
                  {/* 高亮搜索关键词 */}
                  {highlightKeyword(product.name, keyword)}
                </Text>
              </div>
            }
            description={
              <div>
                <div style={{ marginBottom: 8 }}>
                  <Text strong style={{ color: '#ff4d4f', fontSize: 18 }}>
                    ¥{product.price}
                  </Text>
                </div>
                <div style={{ marginBottom: 4, display: 'flex', alignItems: 'center' }}>
                  <Rate allowHalf defaultValue={product.rating || 5} disabled style={{ fontSize: 12 }} />
                  <Text type="secondary" style={{ marginLeft: 8 }}>
                    ({product.reviewCount || 0})
                  </Text>
                </div>
                <div style={{ marginBottom: 4 }}>
                  <Text type="secondary">销量: {product.salesCount}</Text>
                </div>
                {product.categoryName && (
                  <Tag color="blue">{product.categoryName}</Tag>
                )}
              </div>
            }
          />
        </Card>
      </Badge.Ribbon>
    </Col>
  );
  
  // 高亮关键词函数
  const highlightKeyword = (text: string, keyword: string) => {
    if (!keyword) return text;
    
    const regex = new RegExp(`(${keyword})`, 'gi');
    const parts = text.split(regex);
    
    return parts.map((part, index) => 
      regex.test(part) ? (
        <span key={index} style={{ backgroundColor: '#fff566' }}>
          {part}
        </span>
      ) : (
        part
      )
    );
  };
  
  // 渲染筛选抽屉
  const renderFilterDrawer = () => (
    <Drawer
      title="筛选条件"
      placement="right"
      width={300}
      onClose={() => setFilterDrawerVisible(false)}
      open={filterDrawerVisible}
      footer={
        <div style={{ display: 'flex', justifyContent: 'space-between' }}>
          <Button onClick={clearAllFilters}>清除筛选</Button>
          <Button type="primary" onClick={applyFilters}>应用筛选</Button>
        </div>
      }
    >
      <div style={{ marginBottom: 24 }}>
        <Title level={5}>商品分类</Title>
        <CheckboxGroup
          value={selectedCategories}
          onChange={(values) => handleCategoryChange(values as number[])}
        >
          <Space direction="vertical">
            {allCategories.map(category => (
              <Checkbox key={category.id} value={category.id}>
                {category.name}
              </Checkbox>
            ))}
          </Space>
        </CheckboxGroup>
      </div>
      
      <Divider />
      
      <div style={{ marginBottom: 24 }}>
        <Title level={5}>价格区间</Title>
        <Radio.Group
          value={priceRange ? JSON.stringify(priceRange) : ''}
          onChange={(e) => {
            if (e.target.value) {
              const range = JSON.parse(e.target.value) as [number, number];
              handlePriceRangeChange(range);
            }
          }}
        >
          <Space direction="vertical">
            {PRICE_RANGES.map((range, index) => (
              <Radio key={index} value={JSON.stringify(range.value)}>
                {range.label}
              </Radio>
            ))}
          </Space>
        </Radio.Group>
        
        <div style={{ marginTop: 16 }}>
          <Title level={5}>自定义价格区间</Title>
          <Space>
            <InputNumber
              placeholder="最低价"
              value={customPriceRange[0]}
              onChange={(value) => handleCustomPriceChange(0, value)}
              min={0}
              style={{ width: 100 }}
            />
            <Text>至</Text>
            <InputNumber
              placeholder="最高价"
              value={customPriceRange[1]}
              onChange={(value) => handleCustomPriceChange(1, value)}
              min={customPriceRange[0] || 0}
              style={{ width: 100 }}
            />
          </Space>
        </div>
      </div>
      
      <Divider />
      
      <div>
        <Title level={5}>商品评分</Title>
        <Space direction="vertical">
          {[5, 4, 3, 2, 1].map(num => (
            <div 
              key={num} 
              style={{ 
                display: 'flex', 
                alignItems: 'center', 
                cursor: 'pointer',
                padding: '4px 8px',
                borderRadius: 4,
                backgroundColor: selectedRating === num ? '#f0f0f0' : 'transparent'
              }}
              onClick={() => handleRatingChange(num)}
            >
              <Rate disabled defaultValue={num} style={{ fontSize: 16 }} />
              <Text style={{ marginLeft: 8 }}>{num}星及以上</Text>
            </div>
          ))}
        </Space>
      </div>
    </Drawer>
  );
  
  if (searchError) {
    return (
      <div style={{ padding: '20px' }}>
        <Alert
          message="搜索失败"
          description={searchError}
          type="error"
          showIcon
          action={
            <Button size="small" danger onClick={() => dispatch(searchProducts({ keyword, current: 1, size: 12 }))}>
              重试
            </Button>
          }
        />
      </div>
    );
  }
  
  return (
    <div style={{ padding: '20px' }}>
      {/* 搜索结果头部 */}
      <div style={{ marginBottom: 20 }}>
        <Title level={3}>
          搜索结果："{keyword}"
        </Title>
        {!searchLoading && (
          <Text type="secondary">
            共找到 {searchTotal} 个相关商品
          </Text>
        )}
      </div>
      
      {/* 筛选和排序工具栏 */}
      <Card style={{ marginBottom: 20 }}>
        <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
          <Space>
            <Button 
              icon={<FilterOutlined />} 
              onClick={() => setFilterDrawerVisible(true)}
            >
              筛选
            </Button>
            
            <Text strong>排序方式：</Text>
            <Select
              style={{ width: 150 }}
              placeholder="选择排序"
              value={sortBy}
              onChange={handleSortChange}
            >
              <Option value="relevance">相关度</Option>
              <Option value="price_asc">价格从低到高</Option>
              <Option value="price_desc">价格从高到低</Option>
              <Option value="sales_desc">销量从高到低</Option>
              <Option value="rating_desc">评分从高到低</Option>
              <Option value="created_desc">最新发布</Option>
            </Select>
          </Space>
          
          <Space>
            {/* 显示已选筛选条件 */}
            {selectedCategories.length > 0 && (
              <Tag closable onClose={() => {
                setSelectedCategories([]);
                // 直接使用dispatch调用action，而不是调用executeSearch
                dispatch(searchProducts({ 
                  keyword,
                  current: 1,
                  size: 12,
                  minPrice: priceRange ? priceRange[0] : undefined,
                  maxPrice: priceRange ? priceRange[1] : undefined,
                  minRating: selectedRating ?? undefined,
                  sortBy: sortBy !== 'relevance' ? sortBy : undefined
                }));
                
                // 更新URL参数
                const newParams = new URLSearchParams(searchParams);
                newParams.delete('category');
                setSearchParams(newParams);
              }}>
                分类: {allCategories.find(c => c.id === selectedCategories[0])?.name}
              </Tag>
            )}
            
            {priceRange && (
              <Tag closable onClose={() => {
                setPriceRange(null);
                setCustomPriceRange([null, null]);
                // 直接使用dispatch调用action，而不是调用executeSearch
                dispatch(searchProducts({ 
                  keyword,
                  current: 1,
                  size: 12,
                  categoryId: selectedCategories.length === 1 ? selectedCategories[0] : undefined,
                  minRating: selectedRating ?? undefined,
                  sortBy: sortBy !== 'relevance' ? sortBy : undefined
                }));
                
                // 更新URL参数
                const newParams = new URLSearchParams(searchParams);
                newParams.delete('minPrice');
                newParams.delete('maxPrice');
                setSearchParams(newParams);
              }}>
                价格: {priceRange[0]}-{priceRange[1]}元
              </Tag>
            )}
            
            {selectedRating && (
              <Tag closable onClose={() => {
                setSelectedRating(null);
                // 直接使用dispatch调用action，而不是调用executeSearch
                dispatch(searchProducts({ 
                  keyword,
                  current: 1,
                  size: 12,
                  categoryId: selectedCategories.length === 1 ? selectedCategories[0] : undefined,
                  minPrice: priceRange ? priceRange[0] : undefined,
                  maxPrice: priceRange ? priceRange[1] : undefined,
                  sortBy: sortBy !== 'relevance' ? sortBy : undefined
                }));
                
                // 更新URL参数
                const newParams = new URLSearchParams(searchParams);
                newParams.delete('rating');
                setSearchParams(newParams);
              }}>
                评分: {selectedRating}星及以上
              </Tag>
            )}
            
            {(selectedCategories.length > 0 || priceRange || selectedRating) && (
              <Button size="small" onClick={clearAllFilters}>
                清除所有
              </Button>
            )}
          </Space>
        </div>
      </Card>
      
      {/* 搜索结果列表 */}
      <Spin spinning={searchLoading}>
        {searchResults.length > 0 ? (
          <Row gutter={[16, 16]}>
            {searchResults.map(renderProductCard)}
          </Row>
        ) : !searchLoading && (
          <Empty
            description={
              <span>
                没有找到相关商品<br />
                请尝试其他关键词或调整筛选条件
              </span>
            }
            style={{ padding: '60px 0' }}
          >
            <Button type="primary" onClick={() => navigate('/')}>
              返回首页
            </Button>
          </Empty>
        )}
      </Spin>
      
      {/* 分页 */}
      {searchTotal > 0 && (
        <div style={{ textAlign: 'center', marginTop: 32 }}>
          <Pagination
            current={searchCurrent}
            total={searchTotal}
            pageSize={12}
            showSizeChanger
            showQuickJumper
            showTotal={(total, range) => 
              `第 ${range[0]}-${range[1]} 条，共 ${total} 条`
            }
            onChange={handlePageChange}
            onShowSizeChange={handlePageChange}
          />
        </div>
      )}
      
      {/* 筛选抽屉 */}
      {renderFilterDrawer()}
    </div>
  );
};

export default SearchResults;
