import React, { useState } from 'react';
import { Card, Button, Space, Typography, Table, message, Tabs, Input, Select } from 'antd';
import { 
  ProductService, 
  CartService, 
  OrderService, 
  PaymentService, 
  UserService 
} from '../services';

const { Title, Text, Paragraph } = Typography;
const { TabPane } = Tabs;
const { Option } = Select;

// 模拟用户ID
const MOCK_USER_ID = 1;

const ApiTestPage: React.FC = () => {
  const [loading, setLoading] = useState<boolean>(false);
  const [results, setResults] = useState<any[]>([]);
  const [selectedApi, setSelectedApi] = useState<string>('product');
  const [productId, setProductId] = useState<number>(1);
  const [quantity, setQuantity] = useState<number>(1);
  
  // 测试商品API
  const testProductApi = async () => {
    setLoading(true);
    setResults([]);
    
    try {
      // 获取商品列表
      const products = await ProductService.getProducts();
      setResults(prev => [...prev, {
        name: '获取商品列表',
        status: 'success',
        data: products
      }]);
      
      // 获取商品详情
      const product = await ProductService.getProductById(productId);
      setResults(prev => [...prev, {
        name: '获取商品详情',
        status: 'success',
        data: product
      }]);
      
      message.success('商品API测试成功');
    } catch (error) {
      console.error('商品API测试失败:', error);
      message.error('商品API测试失败');
      setResults(prev => [...prev, {
        name: '商品API测试',
        status: 'error',
        data: error
      }]);
    } finally {
      setLoading(false);
    }
  };
  
  // 测试购物车API
  const testCartApi = async () => {
    setLoading(true);
    setResults([]);
    
    try {
      // 获取购物车列表
      const cartItems = await CartService.getCartItems(MOCK_USER_ID);
      setResults(prev => [...prev, {
        name: '获取购物车列表',
        status: 'success',
        data: cartItems
      }]);
      
      // 添加商品到购物车
      const addResult = await CartService.addToCart(MOCK_USER_ID, productId, quantity);
      setResults(prev => [...prev, {
        name: '添加商品到购物车',
        status: 'success',
        data: addResult
      }]);
      
      // 获取购物车数量
      const cartCount = await CartService.getCartItemCount(MOCK_USER_ID);
      setResults(prev => [...prev, {
        name: '获取购物车数量',
        status: 'success',
        data: cartCount
      }]);
      
      message.success('购物车API测试成功');
    } catch (error) {
      console.error('购物车API测试失败:', error);
      message.error('购物车API测试失败');
      setResults(prev => [...prev, {
        name: '购物车API测试',
        status: 'error',
        data: error
      }]);
    } finally {
      setLoading(false);
    }
  };
  
  // 测试订单API
  const testOrderApi = async () => {
    setLoading(true);
    setResults([]);
    
    try {
      // 获取订单列表
      const orders = await OrderService.getOrdersByUserId(MOCK_USER_ID);
      setResults(prev => [...prev, {
        name: '获取订单列表',
        status: 'success',
        data: orders
      }]);
      
      message.success('订单API测试成功');
    } catch (error) {
      console.error('订单API测试失败:', error);
      message.error('订单API测试失败');
      setResults(prev => [...prev, {
        name: '订单API测试',
        status: 'error',
        data: error
      }]);
    } finally {
      setLoading(false);
    }
  };
  
  // 测试支付API
  const testPaymentApi = async () => {
    setLoading(true);
    setResults([]);
    
    try {
      // 获取分期方案
      const plans = await PaymentService.getAvailableInstallmentPlans(1000);
      setResults(prev => [...prev, {
        name: '获取分期方案',
        status: 'success',
        data: plans
      }]);
      
      // 获取用户信用卡
      const cards = await PaymentService.getUserCreditCards(MOCK_USER_ID);
      setResults(prev => [...prev, {
        name: '获取用户信用卡',
        status: 'success',
        data: cards
      }]);
      
      message.success('支付API测试成功');
    } catch (error) {
      console.error('支付API测试失败:', error);
      message.error('支付API测试失败');
      setResults(prev => [...prev, {
        name: '支付API测试',
        status: 'error',
        data: error
      }]);
    } finally {
      setLoading(false);
    }
  };
  
  // 测试用户API
  const testUserApi = async () => {
    setLoading(true);
    setResults([]);
    
    try {
      // 获取用户信息
      const user = await UserService.getUserInfo(MOCK_USER_ID);
      setResults(prev => [...prev, {
        name: '获取用户信息',
        status: 'success',
        data: user
      }]);
      
      // 获取用户地址
      const addresses = await UserService.getUserAddresses(MOCK_USER_ID);
      setResults(prev => [...prev, {
        name: '获取用户地址',
        status: 'success',
        data: addresses
      }]);
      
      // 获取用户积分
      const points = await UserService.getUserPoints(MOCK_USER_ID);
      setResults(prev => [...prev, {
        name: '获取用户积分',
        status: 'success',
        data: points
      }]);
      
      message.success('用户API测试成功');
    } catch (error) {
      console.error('用户API测试失败:', error);
      message.error('用户API测试失败');
      setResults(prev => [...prev, {
        name: '用户API测试',
        status: 'error',
        data: error
      }]);
    } finally {
      setLoading(false);
    }
  };
  
  // 运行所选API测试
  const runSelectedTest = () => {
    switch (selectedApi) {
      case 'product':
        testProductApi();
        break;
      case 'cart':
        testCartApi();
        break;
      case 'order':
        testOrderApi();
        break;
      case 'payment':
        testPaymentApi();
        break;
      case 'user':
        testUserApi();
        break;
      default:
        message.warning('请选择要测试的API');
    }
  };
  
  return (
    <div style={{ padding: '20px', maxWidth: '1200px', margin: '0 auto' }}>
      <Card>
        <Title level={2}>API集成测试</Title>
        <Paragraph>
          此页面用于测试前后端API集成是否正常工作。选择要测试的API类型，然后点击"运行测试"按钮。
        </Paragraph>
        
        <div style={{ marginBottom: 20 }}>
          <Space size="large" align="center">
            <div>
              <Text>选择API类型：</Text>
              <Select 
                value={selectedApi} 
                onChange={setSelectedApi}
                style={{ width: 150, marginLeft: 10 }}
              >
                <Option value="product">商品API</Option>
                <Option value="cart">购物车API</Option>
                <Option value="order">订单API</Option>
                <Option value="payment">支付API</Option>
                <Option value="user">用户API</Option>
              </Select>
            </div>
            
            {(selectedApi === 'cart' || selectedApi === 'product') && (
              <div>
                <Text>商品ID：</Text>
                <Input 
                  type="number" 
                  value={productId} 
                  onChange={e => setProductId(Number(e.target.value))}
                  style={{ width: 100, marginLeft: 10 }}
                />
              </div>
            )}
            
            {selectedApi === 'cart' && (
              <div>
                <Text>数量：</Text>
                <Input 
                  type="number" 
                  value={quantity} 
                  onChange={e => setQuantity(Number(e.target.value))}
                  style={{ width: 100, marginLeft: 10 }}
                />
              </div>
            )}
            
            <Button 
              type="primary" 
              onClick={runSelectedTest}
              loading={loading}
            >
              运行测试
            </Button>
          </Space>
        </div>
        
        <div style={{ marginTop: 20 }}>
          <Title level={4}>测试结果</Title>
          {results.length > 0 ? (
            <div>
              {results.map((result, index) => (
                <Card 
                  key={index} 
                  title={result.name} 
                  style={{ 
                    marginBottom: 10,
                    borderLeft: result.status === 'success' ? '4px solid #52c41a' : '4px solid #f5222d'
                  }}
                  size="small"
                >
                  <div>
                    <Text strong>状态：</Text>
                    <Text type={result.status === 'success' ? 'success' : 'danger'}>
                      {result.status === 'success' ? '成功' : '失败'}
                    </Text>
                  </div>
                  <div style={{ marginTop: 10 }}>
                    <Text strong>响应数据：</Text>
                    <pre style={{ 
                      background: '#f5f5f5', 
                      padding: 10, 
                      borderRadius: 4,
                      maxHeight: 300,
                      overflow: 'auto'
                    }}>
                      {JSON.stringify(result.data, null, 2)}
                    </pre>
                  </div>
                </Card>
              ))}
            </div>
          ) : (
            <Text type="secondary">暂无测试结果，请运行测试</Text>
          )}
        </div>
      </Card>
    </div>
  );
};

export default ApiTestPage;
