import React, { useEffect } from 'react';
import { 
  Row, 
  Col, 
  Card, 
  Button, 
  Typography, 
  Space, 
  Carousel, 
  Image,
  Tag,
  Grid,
  Divider
} from 'antd';
import { 
  ShoppingCartOutlined, 
  FireOutlined, 
  StarOutlined,
  RightOutlined,
  AppstoreOutlined
} from '@ant-design/icons';
import { useDispatch, useSelector } from 'react-redux';
import { useNavigate } from 'react-router-dom';
import { RootState, AppDispatch } from '../store';
import { fetchProducts } from '../store/slices/productSlice';
import { fetchTopCategories } from '../store/slices/categorySlice';
import { addToCartAsync } from '../store/slices/cartSlice';
import { Product } from '../types';

const { Title, Text, Paragraph } = Typography;
const { useBreakpoint } = Grid;

const HomePage: React.FC = () => {
  const dispatch = useDispatch<AppDispatch>();
  const navigate = useNavigate();
  const screens = useBreakpoint();
  
  const { 
    products, 
    loading 
  } = useSelector((state: RootState) => state.products);
  
  const { 
    topCategories 
  } = useSelector((state: RootState) => state.categories);
  
  const { 
    currentUserId 
  } = useSelector((state: RootState) => state.cart);

  useEffect(() => {
    // 获取推荐商品（按销量排序）
    dispatch(fetchProducts({ 
      current: 1, 
      size: 8, 
      sortBy: 'sales_desc' 
    }));
    
    // 获取顶级分类
    dispatch(fetchTopCategories());
  }, [dispatch]);

  // 轮播图数据（使用CSS渐变背景代替外部图片）
  const bannerData = [
    {
      id: 1,
      title: '春季新品上市',
      subtitle: '精选好物，品质保证',
      background: 'linear-gradient(135deg, #667eea 0%, #764ba2 100%)',
      link: '/search?keyword=新品'
    },
    {
      id: 2,
      title: '限时促销活动',
      subtitle: '全场8折，数量有限',
      background: 'linear-gradient(135deg, #f093fb 0%, #f5576c 100%)',
      link: '/search?keyword=促销'
    },
    {
      id: 3,
      title: '会员专享优惠',
      subtitle: '注册会员享更多优惠',
      background: 'linear-gradient(135deg, #4facfe 0%, #00f2fe 100%)',
      link: '/search?keyword=会员'
    }
  ];

  // 快速分类数据
  const quickCategories = topCategories.slice(0, 8);

  // 推荐商品
  const recommendedProducts = products.slice(0, 4);
  
  // 热门商品
  const hotProducts = products.slice(4, 8);

  // 处理加入购物车
  const handleAddToCart = (product: Product) => {
    if (!currentUserId) {
      // 如果未登录，可以提示登录或使用localStorage
      navigate('/');
      return;
    }
    
    dispatch(addToCartAsync({
      userId: currentUserId,
      productId: product.id,
      quantity: 1
    }));
  };

  // 跳转到商品详情
  const handleProductClick = (productId: number) => {
    navigate(`/product/${productId}`);
  };

  // 跳转到分类页面
  const handleCategoryClick = (categoryId: number) => {
    navigate(`/products?categoryId=${categoryId}`);
  };

  // 跳转到全部商品
  const handleViewAllProducts = () => {
    navigate('/products');
  };

  return (
    <div style={{ backgroundColor: '#f5f5f5', minHeight: '100vh' }}>
      {/* 轮播广告区 */}
      <section style={{ marginBottom: 24 }}>
        <Carousel autoplay effect="fade">
          {bannerData.map(banner => (
            <div key={banner.id}>
              <div 
                style={{
                  height: screens.xs ? '200px' : '400px',
                  background: `url(${banner.image}) center/cover`,
                  display: 'flex',
                  alignItems: 'center',
                  justifyContent: 'center',
                  position: 'relative',
                  cursor: 'pointer'
                }}
                onClick={() => navigate(banner.link)}
              >
                <div style={{
                  textAlign: 'center',
                  color: 'white',
                  zIndex: 1
                }}>
                  <Title level={2} style={{ color: 'white', marginBottom: 8 }}>
                    {banner.title}
                  </Title>
                  <Text style={{ color: 'white', fontSize: 16 }}>
                    {banner.subtitle}
                  </Text>
                </div>
                <div style={{
                  position: 'absolute',
                  top: 0,
                  left: 0,
                  right: 0,
                  bottom: 0,
                  background: 'rgba(0,0,0,0.3)'
                }} />
              </div>
            </div>
          ))}
        </Carousel>
      </section>

      <div style={{ padding: '0 24px', maxWidth: 1200, margin: '0 auto' }}>
        {/* 快速分类区 */}
        <section style={{ marginBottom: 32 }}>
          <Card>
            <Row align="middle" style={{ marginBottom: 16 }}>
              <Col>
                <Title level={4} style={{ margin: 0, display: 'flex', alignItems: 'center' }}>
                  <AppstoreOutlined style={{ marginRight: 8, color: '#1890ff' }} />
                  商品分类
                </Title>
              </Col>
            </Row>
            <Row gutter={[16, 16]}>
              {quickCategories.map(category => (
                <Col xs={6} sm={4} md={3} key={category.id}>
                  <div 
                    style={{
                      textAlign: 'center',
                      padding: '16px 8px',
                      borderRadius: 8,
                      backgroundColor: '#fafafa',
                      cursor: 'pointer',
                      transition: 'all 0.3s ease'
                    }}
                    className="category-item"
                    onClick={() => handleCategoryClick(category.id)}
                    onMouseEnter={(e) => {
                      e.currentTarget.style.backgroundColor = '#e6f7ff';
                      e.currentTarget.style.transform = 'translateY(-2px)';
                    }}
                    onMouseLeave={(e) => {
                      e.currentTarget.style.backgroundColor = '#fafafa';
                      e.currentTarget.style.transform = 'translateY(0)';
                    }}
                  >
                    <div style={{
                      width: 40,
                      height: 40,
                      borderRadius: '50%',
                      backgroundColor: '#1890ff',
                      display: 'flex',
                      alignItems: 'center',
                      justifyContent: 'center',
                      margin: '0 auto 8px',
                      color: 'white',
                      fontSize: 18
                    }}>
                      <AppstoreOutlined />
                    </div>
                    <Text style={{ fontSize: 12, display: 'block' }}>
                      {category.name}
                    </Text>
                  </div>
                </Col>
              ))}
            </Row>
          </Card>
        </section>

        {/* 推荐商品区 */}
        <section style={{ marginBottom: 32 }}>
          <Card>
            <Row align="middle" justify="space-between" style={{ marginBottom: 16 }}>
              <Col>
                <Title level={4} style={{ margin: 0, display: 'flex', alignItems: 'center' }}>
                  <StarOutlined style={{ marginRight: 8, color: '#faad14' }} />
                  推荐商品
                </Title>
              </Col>
              <Col>
                <Button 
                  type="link" 
                  icon={<RightOutlined />}
                  onClick={handleViewAllProducts}
                >
                  查看更多
                </Button>
              </Col>
            </Row>
            <Row gutter={[16, 16]}>
              {recommendedProducts.map(product => (
                <Col xs={12} sm={8} md={6} key={product.id}>
                  <Card 
                    hoverable
                    cover={
                      <div style={{ height: 160, overflow: 'hidden' }}>
                        <Image
                          alt={product.name}
                          src={product.imageUrl || 'https://via.placeholder.com/200x160?text=商品图片'}
                          style={{ width: '100%', height: '100%', objectFit: 'cover' }}
                          preview={false}
                        />
                      </div>
                    }
                    actions={[
                      <Button 
                        type="primary" 
                        size="small"
                        icon={<ShoppingCartOutlined />}
                        onClick={(e) => {
                          e.stopPropagation();
                          handleAddToCart(product);
                        }}
                      >
                        加购物车
                      </Button>
                    ]}
                    onClick={() => handleProductClick(product.id)}
                  >
                    <Card.Meta
                      title={
                        <div style={{ height: 40, overflow: 'hidden' }}>
                          <Text ellipsis title={product.name}>
                            {product.name}
                          </Text>
                        </div>
                      }
                      description={
                        <Space direction="vertical" size={4} style={{ width: '100%' }}>
                          <Text type="danger" strong style={{ fontSize: 16 }}>
                            ¥{product.price}
                          </Text>
                          <Text type="secondary" style={{ fontSize: 12 }}>
                            销量 {product.salesCount}
                          </Text>
                        </Space>
                      }
                    />
                  </Card>
                </Col>
              ))}
            </Row>
          </Card>
        </section>

        {/* 热门商品区 */}
        <section style={{ marginBottom: 32 }}>
          <Card>
            <Row align="middle" justify="space-between" style={{ marginBottom: 16 }}>
              <Col>
                <Title level={4} style={{ margin: 0, display: 'flex', alignItems: 'center' }}>
                  <FireOutlined style={{ marginRight: 8, color: '#ff4d4f' }} />
                  热门商品
                </Title>
              </Col>
              <Col>
                <Button 
                  type="link" 
                  icon={<RightOutlined />}
                  onClick={handleViewAllProducts}
                >
                  查看更多
                </Button>
              </Col>
            </Row>
            <Row gutter={[16, 16]}>
              {hotProducts.map((product, index) => (
                <Col xs={12} sm={8} md={6} key={product.id}>
                  <Card 
                    hoverable
                    cover={
                      <div style={{ height: 160, overflow: 'hidden', position: 'relative' }}>
                        <Image
                          alt={product.name}
                          src={product.imageUrl || 'https://via.placeholder.com/200x160?text=商品图片'}
                          style={{ width: '100%', height: '100%', objectFit: 'cover' }}
                          preview={false}
                        />
                        {index < 3 && (
                          <Tag 
                            color={index === 0 ? 'red' : index === 1 ? 'orange' : 'gold'}
                            style={{ 
                              position: 'absolute', 
                              top: 8, 
                              left: 8,
                              margin: 0,
                              fontSize: 12
                            }}
                          >
                            {index === 0 ? '热销王' : index === 1 ? '人气' : '推荐'}
                          </Tag>
                        )}
                      </div>
                    }
                    actions={[
                      <Button 
                        type="primary" 
                        size="small"
                        icon={<ShoppingCartOutlined />}
                        onClick={(e) => {
                          e.stopPropagation();
                          handleAddToCart(product);
                        }}
                      >
                        加购物车
                      </Button>
                    ]}
                    onClick={() => handleProductClick(product.id)}
                  >
                    <Card.Meta
                      title={
                        <div style={{ height: 40, overflow: 'hidden' }}>
                          <Text ellipsis title={product.name}>
                            {product.name}
                          </Text>
                        </div>
                      }
                      description={
                        <Space direction="vertical" size={4} style={{ width: '100%' }}>
                          <Text type="danger" strong style={{ fontSize: 16 }}>
                            ¥{product.price}
                          </Text>
                          <Text type="secondary" style={{ fontSize: 12 }}>
                            销量 {product.salesCount}
                          </Text>
                        </Space>
                      }
                    />
                  </Card>
                </Col>
              ))}
            </Row>
          </Card>
        </section>
      </div>
    </div>
  );
};

export default HomePage;
