// 通用响应结果类型
export interface ApiResponse<T = any> {
  code: number;
  message: string;
  data: T;
}

// 分页结果类型
export interface PageResult<T = any> {
  records: T[];
  total: number;
  current: number;
  size: number;
  pages: number;
}

// 商品类型
export interface Product {
  id: number;
  name: string;
  description: string;
  price: number;
  stockQuantity: number;
  categoryId: number;
  imageUrl: string;
  salesCount: number;
  status: number;
  createdAt: string;
  updatedAt: string;
  categoryName?: string;
}

// 商品分类类型
export interface Category {
  id: number;
  name: string;
  description: string;
  parentId: number | null;
  createdAt: string;
}

// 用户类型
export interface User {
  id: number;
  username: string;
  email: string;
  phone: string;
  createdAt: string;
  updatedAt: string;
}

// 商品查询参数
export interface ProductQueryParams {
  current?: number;
  size?: number;
  categoryId?: number;
  minPrice?: number;
  maxPrice?: number;
  sortBy?: string;
}

// 商品搜索参数
export interface ProductSearchParams {
  keyword: string;
  current?: number;
  size?: number;
  sortBy?: string;
  categoryId?: number;
}

// 购物车商品类型
export interface CartItem {
  product: Product;
  quantity: number;
}

// 订单类型
export interface Order {
  id: string;
  userId: number;
  items: CartItem[];
  totalAmount: number;
  status: string;
  createdAt: string;
}

// 排序选项
export interface SortOption {
  value: string;
  label: string;
} 